-- Дамп данных таблицы `sud_content`
--

INSERT INTO `sud_content` (`id`, `asset_id`, `title`, `alias`, `introtext`, `fulltext`, `state`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) VALUES
(1, 55, 'Vegetables, Dried Foods, and Liquids', 'vegetables-dried-foods-and-liquids', '<h4><span style="line-height: 1.9em; font-size: 12pt;">Bamboo Shoots (Takenoko)</span></h4>\r\n<p style="text-align: justify;"><span style="font-size: 12pt;"><span lang="en-US">&nbsp; &nbsp; &nbsp;</span></span><span lang="en-US"><img style="margin: 15px; float: left;" src="images/articles/bambooshoots.JPG" alt="bambooshoots" /></span><span style="font-family: ''book antiqua'', palatino;"><span style="font-size: 12pt;" lang="en-US"><span style="color: #000000;">The tender shoots of the bamboo plant are harvested only between March and May. Cut into pieces, fresh shoots, with their unique crunchiness and aroma. are a gourmet''s delight. When fresh shoots are not available you can use dried, bottled, or canned varieties. In a few simple steps the preserved varieties can be made to taste almost as good as fresh shoots. Preserved shoots often have a white substance clinging to the pieces. or suspended in the canning water. This is not harmful but it does detract from the shoots'' appearance and taste. To get rid of this substance, wash the shoots in fresh water and boil them for </span></span><span style="color: #000000;"><span style="font-size: 12pt;" lang="en-US">2-3 </span><span lang="en-US"><span style="font-size: 12pt;">minutes. Bamboo shoots should be cooked and seasoned before eat</span></span></span><span style="color: #000000; font-size: 12pt;"><span lang="en-US">ing.</span></span></span></p>\r\n<h4 style="text-align: center;"><span style="font-family: inherit; font-size: inherit; color: #000000; line-height: 1.9em; text-align: justify; background-color: #dddddd;"></span></h4>\r\n<hr />\r\n<h4 style="text-align: justify;"><span style="font-size: 12pt; line-height: 1.9em;">Bean Curd (Tofu)</span></h4>\r\n<p style="text-align: justify;"><span class="img-rounded" style="font-size: small;"><span lang="en-US"><img style="margin: 15px; float: left;" src="images/articles/tofufirm.jpg" alt="tofufirm" width="230" height="184" /><span class="img-rounded" style="font-size: small;"><span lang="en-US"><span style="font-size: 12pt;"><span style="font-family: ''book antiqua'', palatino; color: #000000;">Bean curd, or </span></span></span> </span><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><span class="img-rounded"><span lang="en-US"><em><strong>tofu</strong>, </em></span></span><span class="img-rounded"><span lang="en-US">has become the center of much attention among Western cooking enthusiasts. Besides containing supremely nutritious vegetable protein, the delicate taste and custardlike texture of this soybean product has found its way into many new and interesting Western dishes as well as some familiar ones, for example, hamburger patties and cheesecake. In Japan,. it is one of the staples of the traditional diet. Eaten as is with a variety of flavorings or cooked in any number of ways it is a delicious accompaniment to any Japanese meal.</span></span></span></span></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><span lang="en-US">The </span><span lang="en-US">ivory </span><span lang="en-US">cakes of fresh bean curd themselves are not used when making sushi, but two types of cooked bean curd are. A thin slice of bean curd that has been deep-fried </span><span lang="en-US"><em>(abura age). </em></span><span lang="en-US">seasoned on the sweet side, is used to form a small pouch that is stuffed with sushi rice and other ingredients to make Fox. It is available at most Japanese food shops, either fresh or in cans.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><span lang="en-US"><em>&nbsp; &nbsp; &nbsp;<strong>Kori&middot;tofu </strong></em></span><span lang="en-US">(or </span><span lang="en-US"><em>koya-datu) </em></span><span lang="en-US">is the freeze-dried cousin of the familiar bean curd cake. The bean curd used to make freeze-dried bean curd is denser than the bean curd that IS eaten fresh. It IS also richer in protein. Freeze-dried bean curd is readily identifiable by its spongelike appearance. When buying it, select those that are light yellow in color, finely grained and lustrous, and light in weight. Most freeze-dried bean curd has to be reconstituted before eating, but there is also an "instant" freeze-dried bean curd on the market that can be cooked as it comes out of the package. Sometimes this instant variety comes mixed with seasonings or a soup base to which you add boiling water to ready it for use. Like several other sushi ingredients, freeze-dried bean curd must be cooked and seasoned before eating.</span></span></p>\r\n<h4 style="text-align: center;"><span style="font-size: 12pt; color: #4f81bd; font-family: ''trebuchet ms'', geneva; line-height: 115%;"><span style="font-family: inherit; font-size: inherit; color: #000000; text-align: justify; line-height: 1.9em; background-color: #dddddd;"></span></span></h4>\r\n<hr />\r\n<h4 style="text-align: justify;"><span style="font-size: 12pt;"><span style="color: #333333; line-height: 1.9em;" lang="en-US">Bonito Shavings </span><span style="font-family: ''Times New Roman'', serif;"><span lang="en-US"><em>(Katsuo-bushi)</em></span></span></span></h4>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">&nbsp; &nbsp; &nbsp;Katsuo-bushi</span><span lang="en-US"> or </span><span lang="en-US">okaka</span><span lang="en-US"> is the </span><span style="text-decoration: underline;"><strong><a href="http://en.wikipedia.org/wiki/Japanese_language"><span style="color: #000000; text-decoration: underline;" lang="en-US"><span style="text-decoration: none;">Japanese</span></span></a></strong></span><span lang="en-US"> name for dried, </span><span style="text-decoration: underline;"><a href="http://en.wikipedia.org/wiki/Fermented_fish"><span style="color: #000000; text-decoration: underline;" lang="en-US"><span style="text-decoration: none;">fermented</span></span></a></span><span lang="en-US">, and </span><span style="text-decoration: underline;"><a href="http://en.wikipedia.org/wiki/Smoked_fish"><span style="color: #000000; text-decoration: underline;" lang="en-US"><span style="text-decoration: none;">smoked</span></span></a><a href="http://en.wikipedia.org/wiki/Skipjack_tuna"><span style="color: #000000; text-decoration: underline;" lang="en-US"><span style="text-decoration: none;">skipjack tuna</span></span></a></span><span lang="en-US"> (</span><span lang="en-US">Katsuwonus pelamis</span><span lang="en-US">). Young </span><a href="http://en.wikipedia.org/wiki/Bonito"><span style="color: #000000;" lang="en-US"><span style="text-decoration: none;">bonito</span></span></a><span lang="en-US"> has a similar color and texture and is sometimes used as a cheaper substitute for </span><span style="text-decoration: underline;"><a href="http://en.wikipedia.org/wiki/Skipjack_tuna"><span style="color: #000000; text-decoration: underline;" lang="en-US"><span style="text-decoration: none;">skipjack tuna</span></span></a></span><span lang="en-US"><span style="text-decoration: underline;">,</span> particularly in canned products, but not all countries allow marketing of bonito as skipjack tuna.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">Shaved </span><span lang="en-US">Katsuobushi</span><span lang="en-US"> and dried </span><a href="http://en.wikipedia.org/wiki/Kelp"><span style="color: #000000;" lang="en-US"><span style="text-decoration: none;">kelp</span></span></a><span lang="en-US"> - </span><a href="http://en.wikipedia.org/wiki/Kombu"><span style="color: #000000;" lang="en-US"><span style="text-decoration: none;">kombu</span></span></a><span lang="en-US"> - are the main ingredients of </span><a href="http://en.wikipedia.org/wiki/Dashi"><span style="color: #000000;" lang="en-US"><span style="text-decoration: none;">dashi</span></span></a><span lang="en-US">, a broth that forms the basis of many soups (such as </span><a href="http://en.wikipedia.org/wiki/Miso_soup"><span style="color: #000000;" lang="en-US"><span style="text-decoration: none;">miso</span></span></a><span lang="en-US">) and sauces (e.g., </span><a href="http://en.wikipedia.org/wiki/Soba"><span style="color: #000000;" lang="en-US"><span style="text-decoration: none;">soba</span></span></a><span lang="en-US"> no tsukejiru</span><span lang="en-US">) in </span><a href="http://en.wikipedia.org/wiki/Japanese_cuisine"><span style="color: #000000;" lang="en-US"><span style="text-decoration: none;">Japanese <span style="text-decoration: underline;">cuisine</span></span></span></a><span lang="en-US">.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">Katsuobushi''s distinct </span><a href="http://en.wikipedia.org/wiki/Umami"><span style="color: #000000;" lang="en-US"><span style="text-decoration: none;">umami</span></span></a><span lang="en-US"> flavor comes from its high </span><a href="http://en.wikipedia.org/wiki/Inosinic_acid"><span style="color: #000000;" lang="en-US"><span style="text-decoration: none;">inosinic acid</span></span></a><span lang="en-US"> content. Traditionally made </span><span lang="en-US">katsuobushi</span><span lang="en-US">, known as </span><span lang="en-US">karebushi</span><span lang="en-US">, is deliberately planted with </span><a href="http://en.wikipedia.org/wiki/Aspergillus_glaucus"><span style="color: #000000;" lang="en-US"><span style="text-decoration: none;">Aspergillus glaucus</span></span></a><span lang="en-US"> fungus in order to reduce moisture. Katsuobushi has also been shown to impart a </span><a href="http://en.wikipedia.org/wiki/Kokumi"><span style="color: #000000;" lang="en-US"><span style="text-decoration: none;">kokumi</span></span></a><span lang="en-US"> flavour.</span><a href="http://en.wikipedia.org/wiki/Katsuobushi#cite_note-1"><span style="color: #000000;"><sup><span lang="en-US"><span style="text-decoration: none;"><br /></span></span></sup></span></a></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">Upon being placed on hot food, the heat waves causes the thin and light katsuobushi to move about, giving it a special aesthetic look. It is placed on pizzas to make the top of the pizza seem alive with the "dancing" katsuobushi.</span></span></p>\r\n<p><span style="color: #000000; font-family: ''trebuchet ms'', geneva;">&nbsp;<img style="display: block; margin-left: auto; margin-right: auto;" src="images/articles/bushi.jpg" alt="bushi" width="396" height="144" /></span></p>\r\n<p style="text-align: center;">&nbsp;<span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;" lang="en-US">Katsuobushi is sold in wood-like blocks</span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; font-size: 12pt;"><span style="line-height: 115%; text-align: left;">&nbsp; &nbsp; &nbsp;</span><span style="line-height: 115%; text-align: left; color: #000000;">The bonito fish is beheaded, gutted and filleted. The fatty belly area does not lend well to being preserved, so it is also trimmed off. The fillets are then arranged in a basket and simmered just below boiling for an hour to an hour and a half, depending on the size of the fillets. The rib bones are removed after the fillets have been boiled.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">&nbsp; &nbsp; &nbsp;The fillets are then smoked using oak, pasania, or castanopsis wood; this process can take up to a month. They are smoked for 5&ndash;6 hours in one session, left to rest for one day for the ondensation to rise to the surface, then fired and smoked again the next day, repeating this smoking and resting cycle 12&ndash;15 times. The built up tar from the smoke is cleaned from the surface using a grinder. At this stage the fillets are called </span><span lang="en-US"><em>aragatsuo</em></span><span lang="en-US"> </span><span lang="en-US">&nbsp;and most commonly found in stores shaved and packaged for sale under the name </span><a href="http://en.wikipedia.org/w/index.php?title=Katsuo-kezuri-bushi&amp;action=edit&amp;redlink=1"><span style="color: #000000;" lang="en-US"><em><span style="text-decoration: none;">katsuo-kezuri-bushi</span></em></span></a><span lang="en-US"> or </span><a href="http://en.wikipedia.org/wiki/Hanakatsuo"><span style="color: #000000;" lang="en-US"><em><span style="text-decoration: none;">hanakatsuo</span></em></span></a><span lang="en-US"> -they are not true katsuobushi without the last fermentation stage, but still valued as a good substitute.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">&nbsp; &nbsp; &nbsp;The last stage of creating a katsuobushi is to allow the fish to sun-dry using the assistance of mold. The fillets are sprayed with </span><span lang="en-US"><em>Aspergillus glaucus</em></span><span lang="en-US"> culture and left for 2 weeks in a closed cultivation room. The mold ferments the fillets and also siphons out any residual moisture.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">&nbsp; &nbsp; &nbsp;The mold is continually scraped off, with further sun-drying of the fish increasing hardness and dryness until it resembles a piece of wood, with less than 20% of its original weight. By definition, only fillets that have been treated in this manner may be referred to as a katsuobushi. However, after repeating this process of mold growth and sun-drying at least twice, the katsuobushi can also be called </span><a href="http://en.wikipedia.org/w/index.php?title=Karebushi&amp;action=edit&amp;redlink=1"><span style="color: #000000;" lang="en-US"><em><span style="text-decoration: none;">karebushi</span></em></span></a><span lang="en-US"> ("dried fillet"), and fillets repeating this process more than three times can be called </span><span lang="en-US"><em>honkarebushi</em></span><span lang="en-US"> ("true dried fillet"). When tapped together lightly, they sound almost metallic, and unlike their dull beige outer appearance, when broken open they are a translucent deep ruby color inside. Rarely, very high-end </span><span lang="en-US"><em>honkarebushi</em></span><span lang="en-US"> repeat this drying process for over two years.&nbsp;</span></span></p>\r\n<p style="margin-bottom: 0.19in; line-height: 115%;"><img style="display: block; margin-left: auto; margin-right: auto;" src="images/articles/shaving.jpg" alt="shaving" /></p>\r\n<p style="text-align: center;">Shaving&nbsp;</p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">Katsuobushi kezuriki used to prepare Katsuobushi shavings</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">&nbsp; &nbsp; &nbsp;Traditionally, large chunks of </span><span lang="en-US"><em>katsuobushi</em></span><span lang="en-US"> were kept at hand and shaved when needed with an instrument called a </span><a href="http://en.wikipedia.org/wiki/Katsuobushi_kezuriki"><span style="color: #000000;"><span lang="en-US"><em><span style="text-decoration: none;">katsuobushi kezuriki</span></em></span></span></a><span lang="en-US">, similar to a </span><a href="http://en.wikipedia.org/wiki/Plane_%28tool%29"><span style="color: #000000;"><span lang="en-US"><span style="text-decoration: none;">wood plane</span></span></span></a><span lang="en-US">.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">&nbsp; &nbsp; &nbsp;Katsuobushi is today typically found in bags of small pink-brown shavings. Smaller, thinner shavings, called hanakatsuo, are used as a flavoring and topping for many Japanese dishes, such as </span><a href="http://en.wikipedia.org/wiki/Okonomiyaki"><span style="color: #000000;"><span lang="en-US"><span style="text-decoration: none;">okonomiyaki</span></span></span></a><span lang="en-US">. Larger, thicker shavings, called kezurikatsuo, are used to make the ubiquitous dashi stock.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">&nbsp; &nbsp; &nbsp;The methods and ingredients for making stock differ according to what it is to be used for. Stock that IS the base for a clear soup must necessarily be more carefully seasoned than one that IS used for cooking vegetables.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">In either case, high quality kombu and bonito shavings are essential for making delicious stocks.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><strong><span style="font-size: 14pt;" lang="en-US">Stock No.1</span></strong><span lang="en-US">, for clear soup </span><span lang="en-US"><em>(ichiban dashi)</em></span></span></p>\r\n<p style="text-align: justify;"><strong><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">4 cups water</span></span></strong></p>\r\n<p style="text-align: justify;"><strong><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">1O-cm-square piece of dried kombu</span></span></strong></p>\r\n<p style="text-align: justify;"><strong><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">10-15 grams bonito shavings</span></span></strong></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">Bring the water to a boil in a saucepan. Wipe the kombu clean w</span><span lang="en-US">ith </span><span lang="en-US">a damp cloth. Make a few slashes in the leaf with the tip of a knife and drop in the boiling water. (If you have time to spare, allow the kombu to soak in the water overnight before placing It on the stove.)</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">Just before the water reaches the second boil remove the kombu and set it aside. (It will be used again in the next recipe.) Reduce the heat and stir the shavings into the hot water. Remove the saucepan from the heat. Strain the stock through a sieve lined with</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">a cotton cloth. Set the shavings aside. (They will be used agam</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">m the next recipe.)</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><strong><span style="font-size: 14pt;" lang="en-US">Stock No.2</span></strong><span lang="en-US">, for cooking vegetables and miso soup </span><span lang="en-US"><em>(niban dashi)</em></span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">Combine the kombu and bonito shavings leftover from the preparation of stock no.1 , in a saucepan with 3 1/3 cups of water. Place the pan over high heat. Just before the water reaches </span><span lang="en-US">full </span><span lang="en-US">boil remove the kombu. Lower the heat. Simmer over low heat until about </span><span lang="en-US"><em>20% of </em></span><span lang="en-US">the water has evaporated. Strain through a sieve lined with a cotton cloth.</span></span></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<hr class="system-pagebreak" title="Page 2" alt="Page 2" />\r\n<h4 style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; line-height: 1.9em;" lang="en-US">Burdock </span><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; line-height: 1.9em;" lang="en-US"><em>(Gobo)</em></span></h4>\r\n<h4 class="western"><img style="margin: 15px; float: left;" src="images/articles/275px-Burdockgobo.jpg" alt="275px Burdockgobo" width="230" height="172" /></h4>\r\n<p style="text-align: justify;"><span style="font-size: 12pt;"><span lang="en-US">&nbsp; &nbsp; </span></span><span style="color: #000000; font-size: 12pt;" lang="en-US"><span style="font-family: ''book antiqua'', palatino;">&nbsp;A member of the aster family, this long, slender root vegetable is harvested year-round. But burdock pulled out of the ground in early spring is considered the tastiest. The fibers and slight bitterness of burdock are said to be highly effective in preventing geriatric disorders. To bring out its flavor, burdock is simmered in a seasoned stock.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: inherit; font-size: inherit; line-height: 1.9em; background-color: #dddddd;"></span></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<hr />\r\n<h4 style="text-align: justify;">&nbsp;<span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">Devil''s-Tongue Jelly </span><span lang="en-US"><em>(Konnyaku)</em></span></span></h4>\r\n<p style="text-align: justify;"><span style="color: #000000; font-family: ''book antiqua'', palatino; font-size: 12pt; line-height: 100%;" lang="en-US"><em><img style="margin: 15px; float: left;" src="images/articles/220px-Konnyaku.jpg" alt="220px Konnyaku" width="230" /></em></span><span style="font-size: 12pt; color: #000000;"><span style="text-align: justify; font-family: ''book antiqua'', palatino; line-height: 100%;" lang="en-US"><em><span style="font-family: ''book antiqua'', palatino;">&nbsp; &nbsp; &nbsp;Konnyaku </span></em></span><span style="text-align: justify; font-family: ''book antiqua'', palatino; line-height: 100%;" lang="en-US">is the grayish brown or pale gray cake of gelatinous paste made from the processed tuberous corms of the devil''s tongue plant. It is made up almost wholly of water and is very low in calories. Besides being an ideal food for dieters, it also contains calcium and is alkaline. It is available fresh, canned, or in instant form at most Japanese provisions stores.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: inherit; font-size: inherit; line-height: 1.9em; background-color: #dddddd;"></span></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<hr />\r\n<h4 style="text-align: justify;">&nbsp;<span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">Dried Sardines </span><span lang="en-US"><em>(Niboshi)</em></span></span></h4>\r\n<p class="western"><span style="color: #000000; font-family: ''book antiqua'', palatino; font-size: 12pt; text-align: justify; line-height: 1.9em;" lang="en-US"><img style="margin: 15px; float: left;" src="images/articles/Niboshi_dried_sardines_P_10kg_Box_for.jpg" alt="Niboshi dried sardines P 10kg Box for" width="230" />&nbsp; &nbsp; &nbsp;Sun-dried sardines, called </span><span style="color: #000000; font-family: ''book antiqua'', palatino; font-size: 12pt; text-align: justify; line-height: 1.9em;" lang="en-US"><em>niboshi, </em></span><span style="color: #000000; font-family: ''book antiqua'', palatino; font-size: 12pt; text-align: justify; line-height: 1.9em;" lang="en-US">are used for preparing a stock similar to but more strongly flavored than stock made with bonito shavings (page 6). These small, silvery fish are rich in protein and calcium. The best </span><span style="color: #000000; font-family: ''book antiqua'', palatino; font-size: 12pt; text-align: justify; line-height: 1.9em;" lang="en-US"><em>are </em></span><span style="color: #000000; font-family: ''book antiqua'', palatino; font-size: 12pt; text-align: justify; line-height: 1.9em;" lang="en-US">distinguished by straight whole bodies that&nbsp;</span><span style="color: #000000; font-family: ''book antiqua'', palatino; font-size: 12pt; text-align: justify; line-height: 1.9em;" lang="en-US">glisten and feature a slight touch of green on the spine.</span></p>\r\n<p style="text-align: justify;"><strong><span style="font-family: ''book antiqua'', palatino; color: #000000;" lang="en-US">Dried Sardine Stock</span></strong></p>\r\n<p style="text-align: justify;"><em><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;" lang="en-US">4 cups water</span></em></p>\r\n<p style="text-align: justify;"><em><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;" lang="en-US">15-20 grams dried sardines</span></em></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;" lang="en-US">Snap off the heads and belly parts of all sardines, split each fish in two lengthwise. Rinse lightly in water and place in a saucepan containing cold water, Place pan over heat and bring to a boil. Boil for about 6-7 minutes, constantly skimming the surface for impurities that rise to the surface. Strain. Adding a 1O-cm-square piece of kombu to the water and then removing it just before the water boils will greatly enhance the flavor of this stock.</span></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<hr />\r\n<h3 class="western"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;" lang="en-US">Ginger root (shoga)</span></h3>\r\n<p><span style="font-family: ''book antiqua'', palatino; font-size: 12pt;" lang="en-US"><img style="margin: 15px; float: left;" src="images/articles/ginger.jpg" alt="ginger" width="230" /><span style="color: #000000;">&nbsp; &nbsp; &nbsp;This spice beckons appetites with its pungent flavor and aroma. Its slight sharpness helps to settle the digestive system. Select roots with taut, unwrinkled skin. Grated ginger, also available ready-made in a tube, can be used as a preseasoning or spice. As needle-thin slivers, ginger can be used as a garnish for simmered dishes, salads and soups. Sliced thin, it can be pickled or used to counteract strong-smelling meat and fish.</span></span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<hr />\r\n<h4 style="margin-top: 0.19in; margin-bottom: 0.19in; line-height: 100%;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt;"><span lang="en-US">Kampyo</span></span></h4>\r\n<p style="margin-top: 0.19in; margin-bottom: 0.19in; line-height: 100%;"><img style="margin: 15px; float: left;" src="images/articles/kampio1.jpg" alt="kampio1" width="230" height="172" /><img style="margin: 15px; float: left;" src="images/articles/kampio2.jpg" alt="kampio2" width="220" height="172" />&nbsp;</p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; color: #000000;"><span lang="en-US">&nbsp; &nbsp; &nbsp;</span></span><span style="font-family: ''book antiqua'', palatino; color: #000000;"><span lang="en-US"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt;">Kampyo is one of the most important sushi ingridient.It is Japanese gourd, same family with calabash or big round things attatched behind of Indian instrument Sitar. This heavy fruit is harvested late June, peeled and dried.For cooking, kampyo is washed in warm salted water very well, cooked in boiling water about 1 hour. Once drained, cooked again in soya sauce and sugar untill almost no requid remain in the pan.</span></span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">This food contains vitamine B2, iron and a lot of dietry fiber.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">In Japan,</span><span lang="en-US"><strong>Norimaki</strong></span><span lang="en-US"> ( sushi roll ) means Kampyo roll, if you don''t mention any other name of ingriedients. ( In same way " Flower " means cherry blosomes in Japan.)&nbsp;</span></span><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">In old time, when refrigater was not exist, sushi was not made in home but only in sushi restaurant except Kampyo roll. This roll was popular item for picnic or all kinds of event to bring. </span>Purely the taste of mother.</span></p>\r\n<hr />\r\n<h4 class="western"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt;" lang="en-US">Kombu</span></h4>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US"><img style="margin: 15px; float: left;" src="images/articles/kombu_.jpg" alt="kombu " width="230" />&nbsp; &nbsp; &nbsp;A member of the kelp family, kombu is a versatile pantry ingredient that provides dishes with </span><a href="http://www.thekitchn.com/thekitchn/reading/umami-bombs-use-the-fifth-taste-in-holiday-cooking-038647"><span style="color: #000000;"><span lang="en-US"><span style="text-decoration: none;">umami</span></span></span></a><span lang="en-US"> flavor, nutrients, and minerals. Dried kombu can be used to make broth, added to beans to make them more digestible, and eaten in salads. </span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">&nbsp; &nbsp; &nbsp;Most kombu comes from Hokkaidō, Japan. It is also cultivated in Korea, where it is known as dashima, and China, where it is known as haidai. (Maine kelp is similar and can be substituted for kombu.) Mildly salty and subtly sweet, kombu contains glutamic acid (the basis of monosodium glutamate, or MSG), which enhances flavor and tenderizes proteins. </span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Dried kombu may be found in East Asian markets and health food stores. The strips are often covered with a white powder from natural salts. Just wipe with a damp cloth before cooking. Store kombu in an airtight container away from sunlight and moisture. </span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Here are some of our favorite ways to use this sea vegetable: </span></span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; font-size: 12pt;"><strong><span lang="en-US">To make vegetarian broth</span></strong><span lang="en-US">. Kombu can be used to make a light broth for Asian soups like </span><a href="http://www.thekitchn.com/thekitchn/diy-recipe/diy-recipe-miso-soup-013786"><span lang="en-US"><span style="text-decoration: none;">miso</span></span></a><span lang="en-US">, noodle soup, and tofu soup. To make one quart of broth, fill a pot with 4 cups of water and a 4-6" strip of kombu. Cover and simmer for 20 minutes. Add soy sauce if desired. (Use the leftover strip of kombu in other recipes.) </span></span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; font-size: 12pt;"><strong><span lang="en-US">To make dashi</span></strong><span lang="en-US">. Kombu is combined with bonito to make </span><a href="http://www.thekitchn.com/thekitchn/asian/japanese-cooking-dashi-057749"><span style="color: #00000a;"><span lang="en-US"><span style="text-decoration: none;">dashi</span></span></span></a><span lang="en-US">, an essential stock used in Japanese cooking.&nbsp;</span></span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; font-size: 12pt;"><strong><span lang="en-US">To soften beans</span></strong><span lang="en-US">. The amino acids in kombu help soften beans and make them more digestible. Add a 4-6" strip of kombu to a pot of cooking beans. After an hour or two, the kombu will disintegrate when stirred. (Any stray pieces should be tender enough to eat, or you can remove them.) </span></span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; font-size: 12pt;"><strong><span lang="en-US">In stews</span></strong><span lang="en-US">. Use kombu to enhance the flavor of vegetable stews. It is particularly good with root vegetables like carrots, parsnips, and turnips. </span></span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; font-size: 12pt;"><strong><span lang="en-US">In salads</span></strong><span lang="en-US">. Place kombu in a pot, cover with water, and simmer for an hour or until soft. Cut into strips and add to salads. </span></span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; font-size: 12pt;"><strong><span lang="en-US">As a condiment</span></strong><span lang="en-US">. Roast kombu in a dry skillet over medium heat until crisp. Crumble or grind it into a powder and use it as a salt substitute. Sprinkle it over grains, tofu, and vegetables.&nbsp;</span></span></p>\r\n<hr />\r\n<h4 class="western"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt;">Renkon (lotus root)</span></h4>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt;"><span lang="en-US"><img style="margin: 15px; float: left;" src="images/articles/lotusroot.jpg" alt="lotusroot" width="230" />&nbsp; &nbsp; &nbsp;<span style="color: #000000;">Renkon or lotus root is often used in Nimono dishes (boiled and seasoned vegetables) or Tempura. Technically, they are actually rhizomes or subterranean stems, like ginger, but let&rsquo;s call them roots. Renkon has holes running through the roots, giving it a very unique appearance when cut. (The holes form naturally as air chambers in the root &ndash; not from worms!)</span></span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">The texture of Renkon changes depending on how you cook it.&nbsp; When it&rsquo;s boiled, it becomes more starchy.&nbsp; When it is pan-fried, it is crunchy.&nbsp; You can also grate and fry it, and then it becomes a little sticky like Mochi.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"><span lang="en-US">Renkon is available raw or boiled at Japanese or Asian markets.</span></span></p>\r\n<p>&nbsp;</p>\r\n<hr />\r\n<p>&nbsp;</p>\r\n<h4><span style="font-size: 14px; line-height: 1.9em;">Mirin</span></h4>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><em><span lang="en-US"><img style="margin: 15px; float: left;" src="images/articles/ьшкштюозп.jpg" alt="ьшкштюозп" width="230" height="345" />Mirin&nbsp;</span></em><span lang="en-US">(sweet cooking rice wine) is a sweet and syrupy liquid and it is one of the most important condiments in Japanese cooking.&nbsp; It is a kind of rice wine similar to sake, but with a lower alcohol content (14% instead of 20%).</span></span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span style="line-height: 115%;"><span lang="en-US">In general, there are two common types of mirin &ndash; </span></span><a href="https://www.google.com/search?q=hon+mirin&amp;hl=en&amp;client=opera&amp;hs=HZU&amp;rls=en&amp;channel=suggest&amp;prmd=imvnsfde&amp;tbm=isch&amp;tbo=u&amp;source=univ&amp;sa=X&amp;ei=VLkcT_H4OoiPigKx6KXRCA&amp;ved=0CDcQsAQ&amp;biw=1567&amp;bih=750" style="font-size: 12pt; font-family: ''Times New Roman'', serif; line-height: 115%;"><span style="color: #000000;"><span lang="en-US"><em><span style="text-decoration: none;"><strong>hon mirin</strong></span></em></span></span></a><span style="line-height: 115%;"><span lang="en-US"> (&ldquo;real&rdquo; mirin) and </span></span><a href="http://www.amazon.com/Kikkoman-Aji-Mirin-Sweet-Cooking-Rice/dp/B0002YB210/ref=sr_1_cc_1?s=aps&amp;ie=UTF8&amp;qid=1327282298&amp;sr=8-1-catcorr" style="font-size: 12pt; font-family: ''Times New Roman'', serif; line-height: 115%;"><span style="color: #000000;"><em><span lang="en-US"><strong>mirin-fu chomiryo</strong></span></em><span style="color: #00000a;"><span lang="en-US"></span></span></span></a><span style="line-height: 115%;"><span lang="en-US">(mirin-like condiment.</span></span></span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><em><span lang="en-US">Hon mirin</span></em><span lang="en-US"> consists of 40-50% sugar and 14% alcohol. It is made of glutinous rice, cultured rice, and distilled alcoholic beverage.&nbsp; They are mixed and fermented for about 2 months.</span></span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><em><span lang="en-US">Mirin-like condiment</span></em><span lang="en-US"> contains less than 1% alcohol but claims to have the same taste as </span><em><span lang="en-US">hon mirin</span></em><span lang="en-US">.&nbsp; It is made of starch syrup, rice/cultured rice brewed seasoning, brewed vinegar, acidic components and it&rsquo;s cheaper because it avoids certain alcohol taxes.</span></span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Mirin tenderize and adds a mild sweetness and has deep body and </span><em><span lang="en-US">umami</span></em><span lang="en-US">.&nbsp; It also helps mask the smell of fish and seafood and helps the flavors to &ldquo;sink in&rdquo; to the dish.&nbsp; It also keeps the ingredients from disintegrating because of the sugars and alcohol content.&nbsp; Lastly, mirin adds luster to ingredients which is why it is a key ingredient in teriyaki sauce.</span></span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><strong><span lang="en-US">Substitution:</span></strong><span lang="en-US"> Although it won&rsquo;t be exactly the same, you can substitute mirin with sake and sugar.&nbsp; The ratio of sake and sugar is </span><strong><span lang="en-US">3 to 1</span></strong><span lang="en-US">.&nbsp; Mix 3/4 cup&nbsp; (or 1 Tbsp) good quality drinking sake and 1/4 cup (or 1 tsp) granulated sugar. &nbsp;If you cannot use alcohol in your cooking, Mix 3/4 cup (or 1 Tbsp) water and 1/4 cup (or 1 tsp) granulated sugar.</span></span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"></span></p>\r\n<hr />\r\n<h4><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;">Nagaimo</span></h4>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;">&nbsp;<img style="margin: 15px; float: left;" src="images/articles/nagamo.jpg" alt="nagamo" width="230" height="153" /><span lang="en-US">An essential ingredient in </span><a href="http://www.thekitchn.com/thekitchn/asian/japanese-cooking-okonomiyaki-046692"><span style="color: #000000;"><span lang="en-US"><span style="text-decoration: none;">okonomiyaki</span></span></span></a><span lang="en-US"> is some grated nagaimo in the raw batter. Often found in Asian markets packaged in shrinkwrapped styrofoam or packed in sawdust, nagaimo is a type of yam that is very long and tubular. It''s cut in five to six-inch sections before being packaged. It''s one of the few members of the yam family that can be eaten raw.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;">&nbsp;<span lang="en-US">The outside skin is light brown in color and has hairy roots protruding from tiny knobs on the surface. The skin is peeled off, revealing a white flesh that is crunchy and watery like a jicama, but has a very slimy texture. This can make it hard to grip the yam when grating, so take care to not slip and cut your fingers. Some people find that the mucus gives their skin a little bit of an itchy feeling. I haven''t noticed this personally. There are some Asian cultures that consider nagaimo to be an aphrodisiac.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;">&nbsp;<span lang="en-US">Apart from being used in okonomiyaki, nagaimo root is also grated and added to noodles, soups, and maki rolls.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;"></span></p>\r\n<hr class="system-pagebreak" title="Page 3" alt="Page 3" />\r\n<p>&nbsp;</p>\r\n<h4><span style="font-size: 12pt;">Natto</span></h4>\r\n<p style="text-align: justify;"><img style="margin: 15px; float: left;" src="images/articles/593.jpg" alt="593" width="230" /><span style="font-size: 12pt; font-family: ''book antiqua'', palatino;">&nbsp; &nbsp; &nbsp;The fermented soybean product called natto has a very distinctive&nbsp;aroma and is quite glutinous. It often makes an appearance in the&nbsp;traditional Japanese breakfast. Like most soybean products, it is&nbsp;highly nutritious.</span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<hr />\r\n<h4 style="text-align: left;"><span style="line-height: 1.9em; font-size: 12pt;">Nori</span></h4>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino;"><img style="margin: 15px; float: left;" src="images/articles/nori.jpg" alt="nori" width="230" /><span style="color: #000000;">Nori &nbsp;is the Japanese name for <a href="http://en.wikipedia.org/wiki/Edible_seaweed" title="Edible seaweed"><span style="color: #000000;">edible seaweed</span></a> species of the <a href="http://en.wikipedia.org/wiki/Red_alga" class="mw-redirect" title="Red alga"><span style="color: #000000;">red algae</span></a> genus <a href="http://en.wikipedia.org/wiki/Porphyra" title="Porphyra"><span style="color: #000000;">Porphyra</span></a>, including <a href="http://en.wikipedia.org/w/index.php?title=Porphyra_yezoensis&amp;action=edit&amp;redlink=1" class="new" title="Porphyra yezoensis (page does not exist)"><span style="color: #000000;">P. yezoensis</span></a> and <a href="http://en.wikipedia.org/wiki/Porphyra_tenera" class="mw-redirect" title="Porphyra tenera"><span style="color: #000000;">P. tenera</span></a>. Nori is familiar in the <a href="http://en.wikipedia.org/wiki/United_States" title="United States"><span style="color: #000000;">United States</span></a> and other countries as an ingredient of <a href="http://en.wikipedia.org/wiki/Sushi" title="Sushi"><span style="color: #000000;">sushi</span></a>, being referred to as "nori" (as the Japanese do) or simply as seaweed. Finished products are made by a shredding and rack-drying process that resembles <a href="http://en.wikipedia.org/wiki/Papermaking" title="Papermaking"><span style="color: #000000;">papermaking</span></a>.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Porphyra is also called <a href="http://en.wikipedia.org/wiki/Laver_%28seaweed%29" title="Laver (seaweed)"><span style="color: #000000;">laver</span></a> in Wales and other English-speaking countries.<sup id="cite_ref-1" class="reference"><a href="http://en.wikipedia.org/wiki/Nori#cite_note-1"><span style="color: #000000;"><br /></span></a></sup></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Sheets of dried laver. called nori. are used for rolling up vinegared&nbsp;rice and a number of other ingredients to make many varieties of&nbsp;sushi. To make sheets of nori, seaweed is gathered from the sea,&nbsp;washed, and then hopped into small pieces. After being washed in&nbsp;fresh water to remove any salt. the pieces of seaweed are then&nbsp;poured into a large. square frame and dried. The sheets that result&nbsp;are cut into manageable sizes and packaged. A nutritious food,nori&nbsp;is rich in vitamins A, B1 , B2 ,B 6, and B12, C,and D,as well as iodine,&nbsp;and has been shown highly effective in curbing the formation of&nbsp;cholesterol deposits in blood vessels.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">When selecting nori for sushi, look for sheets that are absolutely&nbsp;dry, highly aromatic, smooth to the touch, shiny and dark, and of&nbsp;even thickness. Sheets with holes or that are brown in color will not&nbsp;do for rolling sushi. Oftentimes, nori comes in a bundle of ten sheets&nbsp;(each sheet measuring 20.5 x 17.5 cm) folded in two, the "fronf"&nbsp;on the outside, and sealed in cellophane.</span></p>\r\n<hr />\r\n<h4><span style="font-size: 12pt;">Shiitake</span></h4>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><img style="margin: 15px; float: left;" src="images/articles/234px-Shiitakegrowing.jpg" alt="234px Shiitakegrowing" width="230" />The earliest written record of shiitake cultivation is seen in the Records of Long Quan County &nbsp;compiled by He Zhan &nbsp;in AD 1209 during the Southern <a href="http://en.wikipedia.org/wiki/Song_Dynasty" class="mw-redirect" title="Song Dynasty"><span style="color: #000000;">Song Dynasty</span></a>.&nbsp;The 185-word description of shiitake cultivation from that literature was later crossed-referenced many times and eventually adapted in a book by a Japanese horticulturist &nbsp;in AD 1796, the first book on shiitake cultivation in Japan.<sup id="cite_ref-Miles_2004_8-0" class="reference"><a href="http://en.wikipedia.org/wiki/Shiitake#cite_note-Miles_2004-8"><span style="color: #000000;"><br /></span></a></sup></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">The Japanese cultivated the mushroom by cutting <a href="http://en.wikipedia.org/wiki/Shii_tree" class="mw-redirect" title="Shii tree"><span style="color: #000000;">shii trees</span></a> with axes and placing the logs by trees that were already growing shiitake or contained shiitake <a href="http://en.wikipedia.org/wiki/Spore" title="Spore"><span style="color: #000000;">spores</span></a>. Before 1982, the Japanese variety of these mushrooms could only be grown in traditional locations using ancient methods.&nbsp;A 1982 report on the budding and growth of the Japan Islands variety revealed opportunities for commercial cultivation in the United States.<sup id="cite_ref-Leatham_1982_9-0" class="reference"><a href="http://en.wikipedia.org/wiki/Shiitake#cite_note-Leatham_1982-9"><span style="color: #000000;"><br /></span></a></sup></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Shiitake mushrooms are now widely cultivated all over the world, and contribute about 25% of total yearly production of mushrooms.<sup id="cite_ref-Vane_2003b_10-0" class="reference"><a href="http://en.wikipedia.org/wiki/Shiitake#cite_note-Vane_2003b-10"><span style="color: #000000;">[10]</span></a></sup> Commercially, shiitake mushrooms are typically grown in conditions similar to their natural environment on either artificial substrate or hardwood logs, such as oak.</span></p>\r\n<p><span id="Culinary_use" class="mw-headline" style="font-size: 12pt; font-family: ''book antiqua'', palatino;">Culinary use</span></p>\r\n<div class="thumb tright">&nbsp;</div>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><span style="line-height: 1.9em;">Fresh and dried shiitake have many uses in the </span><a href="http://en.wikipedia.org/wiki/Asian_cuisine#East_Asia" style="line-height: 1.9em;" title="Asian cuisine"><span style="color: #000000;">cuisines of East Asia</span></a><span style="line-height: 1.9em;">. In Japan, they are served in </span><a href="http://en.wikipedia.org/wiki/Miso_soup" style="line-height: 1.9em;" title="Miso soup"><span style="color: #000000;">miso soup</span></a><span style="line-height: 1.9em;">, used as the basis for a kind of </span><a href="http://en.wikipedia.org/wiki/Vegetarianism" style="line-height: 1.9em;" title="Vegetarianism"><span style="color: #000000;">vegetarian</span></a><span style="line-height: 1.9em;"> </span><em style="line-height: 1.9em;"><a href="http://en.wikipedia.org/wiki/Dashi" title="Dashi"><span style="color: #000000;">dashi</span></a></em><span style="line-height: 1.9em;">, and as an ingredient in many steamed and simmered dishes. In Chinese cuisine, they are often </span><a href="http://en.wikipedia.org/wiki/Saut%C3%A9ing" style="line-height: 1.9em;" title="Saut&eacute;ing"><span style="color: #000000;">saut&eacute;ed</span></a><span style="line-height: 1.9em;"> in vegetarian dishes such as </span><a href="http://en.wikipedia.org/wiki/Buddha%27s_delight" style="line-height: 1.9em;" title="Buddha''s delight"><span style="color: #000000;">Buddha''s delight</span></a><span style="line-height: 1.9em;">. In Thailand, they may be served fried or steamed.</span><sup class="noprint Inline-Template Template-Fact" style="white-space: nowrap;"><br /></sup></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Shiitake are also dried and sold as preserved food. These are rehydrated by soaking in water before using. Many people prefer dried shiitake to fresh, considering that the sun-drying process draws out the <a href="http://en.wikipedia.org/wiki/Umami" title="Umami"><span style="color: #000000;">umami</span></a> flavour from the dried mushrooms. The stems of shiitake are rarely used in Japanese and other <a href="http://en.wikipedia.org/wiki/Cuisine" title="Cuisine"><span style="color: #000000;">cuisines</span></a>, primarily because the stems are harder and take longer to cook than the soft fleshy caps.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">One type of high grade shiitake is called <em>donko</em> in Japaneseю&nbsp;and dōnggū in Chinese, literally "winter mushroom". Another high grade of mushroom is called <em>huāgū</em> in Chinese, literally "flower mushroom", which has a flower-like cracking pattern on the mushroom''s upper surface. Both of these are produced at lower temperatures.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Today, shiitake mushrooms have become popular in other countries.&nbsp;Russia produces and consumes large amounts of them, mostly sold pickled; and the shiitake is slowly making its way into western cuisine.&nbsp;There is a global industry in shiitake production, with local farms in most western countries in addition to large scale importation from China, Japan, Korea and elsewhere.<sup class="noprint Inline-Template Template-Fact" style="white-space: nowrap;"><br /></sup></span></p>\r\n<p style="text-align: justify;"><span style="color: #000000; font-family: ''book antiqua'', palatino; font-size: 12pt; line-height: 1.9em;">Like all mushrooms, shiitakes produce </span><a href="http://en.wikipedia.org/wiki/Vitamin_D2" class="mw-redirect" style="font-family: ''book antiqua'', palatino; font-size: 12pt; line-height: 1.9em;" title="Vitamin D2"><span style="color: #000000;">vitamin D2</span></a><span style="color: #000000; font-family: ''book antiqua'', palatino; font-size: 12pt; line-height: 1.9em;"> upon exposure of the ergosterol to the UVB rays of sunlight or broadband UVB fluorescent tubes.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">While all mushrooms have <a href="http://en.wikipedia.org/wiki/Ergosterol" title="Ergosterol"><span style="color: #000000;">ergosterol</span></a> in and the potential to produce vitamin D2 in such a manner, the transparent white of the shiitake gills permits greater contact of the UVB with ergosterol and very high D2 values can be achieved with exposure to broadband UVB fluorescent tubes.</span><sup id="cite_ref-15" class="reference"><a href="http://en.wikipedia.org/wiki/Shiitake#cite_note-15"><br /></a></sup></p>\r\n<hr />\r\n<h4><span style="font-size: 12pt;">Shiso</span></h4>\r\n<p><span style="color: #000000; font-size: 12pt; font-family: ''book antiqua'', palatino;"><strong><img style="margin: 15px; float: left;" src="images/articles/shiso.jpg" alt="shiso" width="230" />Shiso</strong>&nbsp;-&nbsp;&nbsp;is the now common name&nbsp;for the Asian culinary herb, seed (spice), or entire <a href="http://en.wikipedia.org/wiki/Annual_plant" title="Annual plant"><span style="color: #000000;">annual plant</span></a> of <em><a href="http://en.wikipedia.org/wiki/Perilla_frutescens" title="Perilla frutescens"><span style="color: #000000;">Perilla frutescens</span></a></em><sup id="cite_ref-larkcom_3-0" class="reference"><a href="http://en.wikipedia.org/wiki/Shiso#cite_note-larkcom-3"><span style="color: #000000;">[3]</span></a></sup> variety <em>crispa</em>, belonging to the <a href="http://en.wikipedia.org/wiki/Mint_family" class="mw-redirect" title="Mint family"><span style="color: #000000;">mint family</span></a>.</span></p>\r\n<p><span style="color: #000000; font-size: 12pt; font-family: ''book antiqua'', palatino;">The conventional translation "<strong>beefsteak plant</strong>" is outdated and has fallen into disuse in current media,&nbsp;though it has not passed into complete obsolescence. The genus name <strong>Perilla</strong> is sometimes used as a common name&nbsp;but it is ambiguous, since that term is also inclusive of the wild sesame variety, which is devoid of the distinctive <em>shiso</em> fragrance.</span></p>\r\n<p><span style="color: #000000; font-size: 12pt; font-family: ''book antiqua'', palatino;"><strong><span id="Red_shiso" class="mw-headline">Red shiso</span></strong></span></p>\r\n<p><span style="color: #000000; font-size: 12pt; font-family: ''book antiqua'', palatino;">The purple-red type may be known as <em>akajiso</em>&nbsp;(<em>"red shiso"</em>). The quintessential use is for coloring the pickled plum, or <strong><a href="http://en.wikipedia.org/wiki/Umeboshi" title="Umeboshi"><span style="color: #000000;">umeboshi</span></a></strong>. The shiso leaf turns bright red when it reacts with the <strong><a href="http://en.wikipedia.org/wiki/Umezu" title="Umezu"><span style="color: #000000;">umezu</span></a></strong>, the vinegary brine that wells up from the plums after being pickled in their vats.&nbsp;The red pigment is identified as the Perilla <a href="http://en.wikipedia.org/wiki/Anthocyanin" title="Anthocyanin"><span style="color: #000000;">anthocyanin</span></a>, aka <em>shisonin</em>.&nbsp;The mature red leaves are not very amenable to use as a raw salad leaf. But germinated sprouts <em><strong>me-jiso</strong></em> &nbsp;have been used for years as garnish to accent a Japanese dish such as a plate of <a href="http://en.wikipedia.org/wiki/Sashimi" title="Sashimi"><span style="color: #000000;">sashimi</span></a>.&nbsp;Also used are the <em>hanaho</em> or <em><strong>hojiso</strong></em>, which are sprigs or stalks studded with tiny-cupped flowers and forming seeds.&nbsp;The tiny pellets of buds and seed pods can be scraped off using the chopstick or fingers and mixed into the soy sauce dip to add the distinct spicy flavor.&nbsp;The sprouts and flowerheads of the green variety are also used the same way.</span></p>\r\n<h3><span style="color: #000000; font-size: 12pt; font-family: ''book antiqua'', palatino;"></span></h3>\r\n<p><span style="color: #000000; font-size: 12pt; font-family: ''book antiqua'', palatino;"><strong><span id="Green_shiso" class="mw-headline">Green shiso</span></strong></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Bunches of green shiso leaves packaged in styrofoam trays are now familiar sights on the supermarket shelves in Japan, as well as in Japanese food markets in the West. But production in earnest as leafy herb did not begin until the 1960s.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">One anecdote is that around 1961, a certain cooperative or guild of <em>tsuma</em>&nbsp;&nbsp;commodities based in <a href="http://en.wikipedia.org/wiki/Shizuoka_Prefecture" title="Shizuoka Prefecture"><span style="color: #000000;">Shizuoka Prefecture</span></a> picked large-sized green leaves of shiso and shipped them out to the Osaka market, and gained popularity, so that <em>ōba</em> became the trade name for bunches of picked green leaves forever after.<sup id="cite_ref-11" class="reference"><a href="http://en.wikipedia.org/wiki/Shiso#cite_note-11"><span style="color: #000000;"><br /></span></a></sup></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Green-leaf shlso has a unique aroma and taste and is used in many&nbsp;vaneties of sushi, in dressed, saladllke dishes, and in tempura. Red-leal shiso&nbsp;is used for coloring umeboshi. ginger, and various other&nbsp;Japanese pickles. The shiso plant. with its beautiful serrated leaves, &nbsp;reaches maturity in the summer. The delicately colored flowering&nbsp;seed pods are used as a garnish on a plate of sashimi.</span></p>\r\n<hr />\r\n<h4><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Soy Sauce, Shoyu, and Tamari</span></h4>\r\n<div class="copy-paste-block" style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><img style="margin: 15px; float: left;" src="images/articles/soy.jpg" alt="soy" width="230" />Until the 1960s, the only type of soy sauce available was the mass-produced, commercial variety. In the mid-1960s, George Ohsawa, father of the macrobiotic diet, introduced the natural, Japanese product to North America. This is what is commonly found in natural foods stores, inaccurately labeled tamari. What we buy as tamari (or in some instances labeled shoyu-tamari) is actually what is known in Japan as shoyu. Tamari in Japan is a by-product of miso and is a thicker, stronger tasting sauce. True tamari is rare in this country, so when you buy a product labeled tamari, chances are very good that you are really buying shoyu, or natural soy sauce.</span><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Natural soy sauce is made from a combination of soybeans, roasted hard red wheat, sea salt, and water in more or less equal proportions. The mixture is ideally allowed to ferment in cedar vats for at least 18 months. When fully aged, the mixture is pressed to extract the liquid. Results vary according to specific recipes and regional preferences in Japan and China. </span></div>\r\n<div class="copy-paste-block" style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">The color of soy sauce ranges from light amber to black, and the flavor from pungent and salty to a more mellow salty-sweet. The quality and precise proportions of ingredients, the length of time aged, and the vats used in fermentation all contribute to differences in the flavor, color, and aroma of the sauce.</span><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Mass-produced commercial soy sauces are made both in North America and in the Orient. The processes used to manufacture these are markedly different from the traditional ones used to create natural soy sauce, the most important of which are the following:&nbsp;</span><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Mass-produced soy sauces are made from chemically produced defatted soybean pulp. This process strips soybeans of much flavor and some nutrition.</span><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">By using temperature controls, the fermentation process is artificially speeded up to be accomplished in no more than 3 to 4 months, which some argue, does not allow as fine a flavor to develop.</span><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Chemicals and coloring agents are sometimes used to enhance the product. Because the shoyu is a product of naturally occurring biochemical processes, the compositions of the natural and mass-produced products are very different.</span><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">The Japanese-style natural soy sauce (shoyu) that is sold in natural-food stores has a more full-bodied flavor and tastes less salty than commercial soy sauce. Also available are a reduced-sodium natural soy sauce, which is allowed to ferment for 3 years to develop additional flavor (and again, by means of a much different process than mass-produced low-salt soy sauce), and a wheat-free natural soy sauce for those with wheat intolerance. Stored at room temperature, soy sauce will keep indefinitely.</span><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Natural soy sauce has been shown to contain enzymes that aid in digestion and promote the production of beneficial bacterial cultures in the intestinal tract. The amino acids found in natural soy sauce complement those found in grains.</span></div>\r\n<hr class="system-pagebreak" title="Page 4" alt="Page 4" />\r\n<div class="copy-paste-block" style="text-align: justify;">&nbsp;</div>\r\n<h4 class="entry-title"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Takuan/Japanese Pickled Daikon</span></h4>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><img style="margin: 15px; float: left;" src="images/articles/takuan-pic.jpg" alt="takuan pic" width="230" />Takuan, also known as takuwan or takuan-zuke, is a popular traditional Japanese pickle. It is made from daikon radish. In addition to being served alongside other types of tsukemono/Japanese-style pickles in traditional Japanese cuisine, takuan is also enjoyed at the end of meals as it is thought to aid digestion.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Takuan is made by first hanging a daikon radish in the sun for a few weeks until it becomes flexible. Next, the daikon is placed in a pickling crock and covered with a mix of salt, rice bran, optionally sugar, daikon greens, kombu/Dry seaweed, and perhaps chilli pepper and/or dried persimmon peels/even flowers for colouring. A weight is then placed on top of the crock, and the daikon is allowed to pickle for several months. The finished takuan is usually yellow in colour, although most mass-produced takuan rely on food coloring for this effect.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Takuan is popular also in South Korea, and is called danmuji. It is used as a filling for gimbap, or as an accompaniment to Korean dishes, typically jajangmyeon.</span></p>\r\n<hr />\r\n<h4><span style="font-size: 12pt; color: #000000;">Trefoil (mitsuba)</span></h4>\r\n<p><span style="font-size: 12pt; font-family: ''book antiqua'', palatino;"><img style="margin: 15px; float: left;" src="images/articles/mitsuba.jpg" alt="mitsuba" width="230" /><span style="color: #000000;">This delicate herb is high in carotene and vitamin C. In order to preserve its fragrance, it should never be more than briefly parboiled. Commonly used in salads, custard dishes and clear soups to add aroma, trefoil also is used as a garnish for <em>nabe</em> (refers to a variety of communal one-pot meals) dishes.</span></span><br /><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><sup id="cite_ref-okashin_13-0" class="reference"></sup></span></p>\r\n<p><span style="color: #000000;"><strong><em><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; line-height: 1.9em;">Substitute for Mitsuba</span></em></strong></span></p>\r\n<div class="ingredient-summary">\r\n<p><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Watercress</span></p>\r\n</div>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<hr />\r\n<h4><span class="topic" style="font-size: 12pt; font-family: ''book antiqua'', palatino;"><span class="topic"><span class="topic"><span class="topic">&nbsp;UMEBOSHI</span></span></span></span></h4>\r\n<p><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><img style="margin: 15px; float: left;" src="images/articles/umberboshi.jpg" alt="umberboshi" width="230" />Besides their dramatic flavor, Japanese pickled plums have remarkable medicinal qualities. Their powerful acidity has a paradoxical alkalinizing effect on the body, neutralizing fatigue, stimulating the digestion, and promoting the elimination of toxins. This is the Far Eastern equivalent to both aspirin and apple; not only is it a potent hangover remedy for mornings after; more than that, an umeboshi a day is regarded as one of the best preventive medicines available.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Like many of Japan''s ancient medicinal foods, the origin of the pickled plum is obscure. One theory traces it to China, where a dried smoked plum, or ubai, was discovered in a tomb built over two thousand years ago. The ubai is one of China''s oldest medicines and is still used for a variety of medical purposes such as counteracting nausea, reducing fevers, and controlling coughs.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">The oldest Japanese record of pickled plums being used as a medicine is in a medical text written about one thousand years ago. Umeboshi were used to prevent fatigue, purify water, rid the body of toxins, and cure specific diseases such as dysentery, typhoid, and food poisoning. Slowly, extensive folklore developed about umeboshi''s ability to prevent and cure certain diseases.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">During Japan''s furious samurai period, which lasted through most of the Middle Ages, the pickled plum was the soldier''s most important field ration. It was used to flavor foods such as rice and vegetables, and its high acidity made it an excellent water and food purifier, as well as an effective antidote for battle fatigue.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Almost 200 years ago, the Japanese began experimenting with ways to concentrate the healing powers of umeboshi. Finally, a dark liquid called bainiku ekisu (plum extract) was developed. To make the extract, sour green ume plums are slowly cooked down to obtain their most active ingredients in a highly concentrated form. The resulting dark, sticky, thick liquid is usually mixed with hot water and honey and is drunk as a tonic. Dried plum extract is also formed into pills, called meitan. In both plum extract and meitan, the plums'' citric acid content is concentrated tenfold, which is equivalent to about twenty-five times the content found in lemon juice.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Many natural healers around the world feel that these concentrated forms of Japanese plums are among the world''s most effective natural medicines. Moreover, they do not have the high salt content of pickled plums.&nbsp;</span></p>\r\n<p>&nbsp;</p>\r\n<hr />\r\n<h4><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><span style="line-height: 26.6000003814697px;">Rice vinegar (</span><em style="line-height: 26.6000003814697px;">su</em><span style="line-height: 26.6000003814697px;">)</span></span></h4>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><img style="margin: 15px; float: left;" src="images/articles/rice-vinegar-2.jpg" alt="rice vinegar 2" width="230" />Rice vinegar (<em>su</em>) is golden and less harsh-tasting than regular white vinegar and cider vinegars, which suits the generally delicate flavours of Japanese salad dishes. <em>Sushi</em> <em>su</em> is simply rice vinegar with sugar and salt pre-added for convenience when making <em>sushi</em>.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><em style="line-height: 1.9em;">Su </em><span style="line-height: 1.9em;">is famously used to season </span><em style="line-height: 1.9em;">sushi</em><span style="line-height: 1.9em;"> rice, but its most common use is to flavour and preserve foods in </span><em style="line-height: 1.9em;">sunomono</em><span style="line-height: 1.9em;"> and pickle preparations.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><span style="line-height: 1.9em;">For versatility, I recommend buying regular rice vinegar. </span><em style="line-height: 1.9em;">Sushi</em><span style="line-height: 1.9em;"> </span><em style="line-height: 1.9em;">su </em><span style="line-height: 1.9em;">is preseasoned and convenient for making </span><em style="line-height: 1.9em;">sushi</em><span style="line-height: 1.9em;"> or as a shortcut in </span><em style="line-height: 1.9em;">sunomono </em><span style="line-height: 1.9em;">preparations or salad dressings. However, </span><em style="line-height: 1.9em;">sushi</em><span style="line-height: 1.9em;"> vinegars often contain high-fructose corn syrup and chemical additives rather than simple sugar and salt, which are easy enough to addon your own.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">A powdered form of <em>sushi</em> vinegar, <em>sushinoko</em> is probably the most popular method used by housewives in Japan. It has a stronger flavour than liquid vinegars and is simply sprinkled onto rice while hot and mixed in. Be careful when using it &ndash; it stings if you breathe it in or get it in your eyes.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><strong>Storage: </strong>Keep tightly capped in a cool, dark place or in the fridge.</span></p>\r\n<p style="text-align: left;">&nbsp;</p>\r\n<p style="text-align: left;">&nbsp;</p>\r\n<p style="text-align: left;">&nbsp;</p>\r\n<hr />\r\n<h4><span style="font-size: 12pt; font-family: ''book antiqua'', palatino;">Wasabi</span></h4>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><img style="margin: 15px; float: left;" src="images/articles/was.jpg" alt="was" width="230" />Wasabi is a <a href="http://www.japan-guide.com/e/e2346.html" target="_top"><span style="color: #000000;">root vegetable</span></a> known in English as Japanese horseradish. It is most famous in the grated form of a green paste used as condiment for <a href="http://www.japan-guide.com/e/e2044.html" target="_top"><span style="color: #000000;">sashimi</span></a> (raw seafood) and <a href="http://www.japan-guide.com/e/e2038.html" target="_top"><span style="color: #000000;">sushi</span></a>. However, wasabi is also used for many other Japanese dishes. In <a href="http://www.japan-guide.com/e/e2073.html" target="_top"><span style="color: #000000;">supermarkets</span></a>, wasabi is widely available as a paste or in powder form. Wasabi powder has to be mixed with water to become a paste. Wasabi has a strong, hot flavor which dissipates within a few seconds and leaves no burning aftertaste in one''s mouth.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Many "wasabi" powder and paste products that are available in <a href="http://www.japan-guide.com/e/e2073.html"><span style="color: #000000;">supermarkets</span></a> (and even some restaurants) contain only very little or no real wasabi at all and are made of colored horseradish instead. This is due to the fact that cultivation of real wasabi is relatively difficult and expensive. &nbsp;&nbsp;</span></p>', '', 1, 8, '2015-03-07 21:38:18', 259, '', '2015-03-16 13:25:01', 259, 0, '0000-00-00 00:00:00', '2015-03-07 21:38:18', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"0","show_tags":"0","show_intro":"","info_block_position":"","show_category":"0","link_category":"","show_parent_category":"0","link_parent_category":"","show_author":"0","link_author":"","show_create_date":"0","show_modify_date":"0","show_publish_date":"0","show_item_navigation":"0","show_icons":"0","show_print_icon":"0","show_email_icon":"0","show_vote":"0","show_hits":"0","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"1","show_article_options":"","show_urls_images_backend":"1","show_urls_images_frontend":"1"}', 251, 3, '', '', 1, 399, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '');
INSERT INTO `sud_content` (`id`, `asset_id`, `title`, `alias`, `introtext`, `fulltext`, `state`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) VALUES
(2, 57, 'Eggs', '222', '<p>&nbsp;</p>\r\n<h4 class="western"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Eggs </span><span lang="en-US">(Tamago)</span></span></h4>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">&nbsp; &nbsp; &nbsp;The slightly sweet flavor and bright yellow color of an omelet serves to balance the taste and appearance of a sushi meal. Just as there are many varieties of sushi so there </span><span lang="en-US">are </span><span lang="en-US">many ways </span><span lang="en-US">to </span><span lang="en-US">prepare eggs to complement this rice dish. But in general, only three styles are used, all of them omelets: </span><span lang="en-US"><em>a </em></span><span lang="en-US">thin omelet, a rolled omelet, and a thick omelet made with ground fish meat. These omelets are usually cooked in the special square omelet pan. But </span><span lang="en-US">a </span><span lang="en-US">standard round frying pan about </span><span lang="en-US">25-26 </span><span lang="en-US">cm in diameter (and not tooheavy) can be used in its stead. The recipe that follows gives instructions for making a thin omelet with a conventional frying pan.</span></span></p>\r\n<h4 class="western"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;" lang="en-US">Thin omelet (usuyaki tamago)</span></h4>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">&nbsp; &nbsp; &nbsp;Japanese people love eating eggs in many ways. One of the most popular uses for the egg is to make a very thin omelet called </span><em><span lang="en-US">usuyaki tamago</span></em><span lang="en-US"> (literally, thinly cooked egg). Usuyaki tamago is used julienned as a garnish, or as a wrapper for sushi rice and other things.</span></span></p>\r\n<p><span style="color: #000000; font-family: ''book antiqua'', palatino; font-size: 12pt; line-height: 1.9em;"><img style="margin: 10px auto; display: block;" src="images/articles/eggs/1.jpg" alt="1" />&nbsp;</span></p>\r\n<h3 class="western"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Japanese thin omelette (</span><em><span lang="en-US">usuyaki tamago</span></em><span lang="en-US">)</span></span></h3>\r\n<ul>\r\n<li><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;">1 egg</span></li>\r\n<li><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;">1 Tbs. water or <a href="http://www.justhungry.com/japanese/index.html"><span style="color: #000000;">dashi stock</span></a></span></li>\r\n<li><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;">1 tsp. sugar</span></li>\r\n<li><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;">dash of salt</span></li>\r\n<li><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;" lang="en-US">1 tsp. cornstarch or potato starch (_katakuriko_), dissolved in 1 Tbs. water - optional</span></li>\r\n<li><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;" lang="en-US">peanut oil or similar flavorless oil for cooking</span></li>\r\n</ul>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Beat the egg and water or dashi together. Add the sugar and salt and beat until dissolved. The cornstarch is optional, but it does add some more strength and stability fo the thin egg. You may want to use cornstarch when you are making </span><em><span lang="en-US">usuyakitamago</span></em><span lang="en-US"> for wrapping something in. </span></span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">To ensure a very smooth egg batter, strain the beaten egg mixture through a sieve or a large-mesh tea strainer.</span></span></p>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/eggs/2.jpg" alt="2" width="500" /></p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Heat up a nonstick frying pan over a low-medium heat and coat with a little oil: Pour some into the pan, then use a paper towel to spread it around and wipe up any excess oil.</span></span></p>\r\n<p><img style="margin: 10px auto; display: block;" src="images/articles/eggs/3.jpg" alt="3" width="500" /></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Add a little of the egg mixture (how much depends on the size of your frying pan, but for a small 18cm/6 inch pan allow about 1/8th cup) and rapidly swirl it around until it just coats the bottom.</span></span></p>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/eggs/4.jpg" alt="4" width="500" /></p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Cook on low heat just until the egg is set - this should only take a minute or so. It&rsquo;s done when the edges are dry and the top is just about cooked. </span></span></p>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/eggs/5.jpg" alt="5" width="500" /></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Loosen up the edges with a spatula, then flip the pan upside down onto a plate - the omelette should flop out, like a crepe. </span>Let cool. </span></p>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/eggs/6.jpg" alt="6" width="500" /></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">You should get about 3 </span><em><span lang="en-US">usuyaki tamago</span></em><span lang="en-US"> per egg. If you find the pan is getting too hot and the egg is browning too much, cool off the pan by pressing it lightly on a folded moistened kitchen towel. </span></span></p>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/eggs/7.jpg" alt="7" width="500" /></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">To use as garnish, fold 1 or 2 </span><em><span lang="en-US">usuyaki tamago</span></em><span lang="en-US"> over into thirds, and cut into thin strips or julienne with a sharp knife. This is called </span><em><span lang="en-US">kinshi tamago</span></em><span lang="en-US"> (</span>錦糸卵）<span lang="en-US">, scattered on the top of sushi, noodles, and so on.</span></span></p>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/eggs/8.jpg" alt="8" width="500" /></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">You can also use the whole </span><em><span lang="en-US">usuyaki tamago</span></em><span lang="en-US"> as a wrapping - the bright yellow color makes anything very pretty. An easy way is to just fold it into quarters, as for these </span><a href="http://www.justhungry.com/shell-shaped-sushi-hamaguri-zushi-girls-festival"><span style="color: #000000;"><span lang="en-US"><span style="text-decoration: none;">shell shaped sushi</span></span></span></a><span lang="en-US">. A slightly more tricky way is to wrap a filling (such as some flavored sushi rice or a small onigiri) in a beggar&rsquo;s purse shape. Here I have used a blanched parsley stalk as the tie. </span></span></p>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/eggs/9.jpg" alt="9" /></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">This wrapping shape is called </span><em><span lang="en-US">chakin</span></em><span lang="en-US">; sushi in this shape is called </span><em><span lang="en-US">chakin zushi</span></em><span lang="en-US">. For this and other wrapping methods, the more stable version of </span><em><span lang="en-US">usuyaki tamago</span></em><span lang="en-US"> with some cornstarch in the egg mixture is recommended. </span></span></p>\r\n<h4 class="western"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;" lang="en-US">1 egg tamagoyaki (Japanese rolled omelet)</span></h4>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;" lang="en-US"></span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><a href="http://www.justhungry.com/tamagoyaki"><span style="color: #000000;" lang="en-US">Tamagoyaki</span></a><span lang="en-US">, the slightly sweet rolled Japanese omelette, is a standby protein item for bentos. It tastes great at room temperature, is fairly easy to make (once you&rsquo;ve done it a few times), and is cheap too. Plus the cheery yellow color brightens up any bento box. </span></span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">There is one drawback with tamagoyaki: unless you have a tiny tamagoyaki pan (which is a single-purpose piece of kitchen equipment, something I try to avoid stocking in my not-so-large kitchen), you need to make it with a least 2, preferably 3 or more, eggs, to produce the distinctive multilayers of egg. This is fine if you&rsquo;re making bentos for two or more people, but when you&rsquo;re making bento for one you may not necessarily want to eat 2 eggs at a time. And tamagoyaki held in the fridge for more than a day never tastes as nice. </span></span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">This method of making a 1-egg tamagoyaki in a normal small frying pan was in a recent issue of </span><em><span lang="en-US">Kyou no ryouri</span></em><span lang="en-US"> (Today&rsquo;s Cooking), my favorite Japanese food magazine. I&rsquo;ve tried it out a few times now, and I&rsquo;m totally sold on it. It does make a slightly flatter tamagoyaki than a multi-egg one, but it still has those nice layers. </span></span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Here&rsquo;s how to make it step by step. </span></span></p>\r\n<h3 class="western"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;" lang="en-US">Recipe: 1-egg tamagoyaki</span></h3>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">You can also use the </span><a href="http://www.justhungry.com/tamagoyaki"><span style="color: #000000;" lang="en-US">basic tamagoyaki recipe</span></a><span lang="en-US"> and reduce the ingredients to one quarter. </span></span></p>\r\n<ul>\r\n<li><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;">1 &lsquo;large&rsquo; egg</span></li>\r\n<li><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;">1 Tbs. water</span></li>\r\n<li><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;" lang="en-US">1 tsp. soy sauce (regular or light-colored; here I used regular, since that&rsquo;s all I had in stock)</span></li>\r\n<li><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;">1/2 tsp. sugar</span></li>\r\n<li><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;" lang="en-US">1 Tbs. bonito flakes (optional, for added flavor) </span></li>\r\n<li><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;">Vegetable oil for cooking</span></li>\r\n</ul>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Mix all the ingredients together well with a fork or chopsticks. Heat up a small (6 inch or 15cm) non-stick frying pan and spread thinly with oil (or use a non-stick cooking spray). </span></span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">From this point on, it only takes about a minute and a half! </span></span></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Once the pan is hot (if you put a droplet of water in, it dances and evaporates immediately), pour in the egg. </span></span></p>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/eggs/10.jpg" alt="10" width="500" /></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Stir gently with a fork or chopsticks until it&rsquo;s half-set. </span></span></p>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/eggs/11.jpg" alt="11" width="500" /></p>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Fold in half with a spatula.&nbsp;</span></span></p>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/eggs/12.jpg" alt="12" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Tidy up the other side a bit with the spatula.</span></span></p>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/eggs/13.jpg" alt="13" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Fold the one third of the egg over with the spatula. </span>Press down.&nbsp;</span></p>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/eggs/14.jpg" alt="14" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Fold the other end of the egg over with the spatula. Press the whole thing down.&nbsp;</span></span></p>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/eggs/15.jpg" alt="15" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Flip over, and press again. Remove from the heat before it browns too much. (If you use light colored soy sauce, it won&rsquo;t get as brown.)</span></span></p>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/eggs/16.jpg" alt="16" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">Cut in half and put cut side up, it is nicely multi-layered.</span></span></p>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/eggs/17.jpg" alt="17" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="en-US">A 1-egg tamagoyaki is only about 100 calories and is great as a secondary protein, paired with a small piece of fish, a tiny </span><a href="http://justbento.com/handbook/recipe-collection-mains/tuna-tofu-miso-mini-burgers"><span style="color: #000000;"><span lang="en-US"><span style="text-decoration: none;">tuna tofu burger</span></span></span></a><span lang="en-US"> or </span><a href="http://www.justhungry.com/black-bean-vegan-miniburgers"><span style="color: #000000;"><span lang="en-US"><span style="text-decoration: none;">black bean mini burger</span></span></span></a><span lang="en-US"> and so on.&nbsp;</span></span></p>\r\n<p style="text-align: justify;">&nbsp;</p>', '', 1, 8, '2015-03-12 07:16:51', 259, '', '2015-03-13 10:04:40', 259, 0, '0000-00-00 00:00:00', '2015-03-12 07:16:51', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 8, 2, '', '', 1, 32, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(3, 58, 'Sushi Rice', 'rice', '<div class="step">\r\n<h2><span style="color: #000000;"><em><span id="Ingredients" class="mw-headline" style="font-family: ''book antiqua'', palatino; font-size: 12pt;">Ingredients</span></em></span></h2>\r\n<div id="ingredients" class="section_text">\r\n<ul>\r\n<li>\r\n<div class="checkbox-text"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">2 2/3 cups sushi rice or short-grain rice</span></div>\r\n</li>\r\n<li>\r\n<div class="checkbox-text"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">2 1/2 cups water</span></div>\r\n</li>\r\n<li>\r\n<div class="checkbox-text"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">3 tbs. rice vinegar</span></div>\r\n</li>\r\n<li>\r\n<div class="checkbox-text"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">2 tbs. sugar</span></div>\r\n</li>\r\n<li>\r\n<div class="checkbox-text"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">1 1/2 tsp. salt</span></div>\r\n</li>\r\n</ul>\r\n</div>\r\n</div>\r\n<div class="step"><img style="display: block; margin: 10px auto;" src="images/articles/rice/0.jpg" alt="0" width="500" /></div>\r\n<p class="step"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><strong>Purchase the correct rice.</strong> Sushi is usually made with special Japanese white hulled sushi rice, commonly called sushi rice. It is a high quality short grain that is sticky and slightly sweet rice (not to be mistaken for glutinous rice).</span></p>\r\n<ul style="list-style-type: circle;">\r\n<li style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">For best results, go to Asian stores and ask specifically for sushi rice. High quality rice will have very few broken grains. Real sushi rice has a good balance of starches (amylose and amylopectin) so that the rice sticks together when you''re <a href="http://www.wikihow.com/Eat-with-Chopsticks" title="Eat with Chopsticks"><span style="color: #000000;">using chopsticks</span></a> to bring it from the plate to your mouth. Most of the time it will be labeled "Sushi Rice". Equipment and seasonings such as bamboo sushi-making mats, bamboo spatula,nori sheets and sushi vinegar (Asian white vinegar that has been sweetened is also useable) can also be found there.</span></li>\r\n<li style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">If "Sushi Rice" is unavailable, the closest alternative is Dongbei rice (a strain native to North-east China whose natural environment resembles Japan''s cold climate). Its level of sweetness and stickiness is extremely similar to sushi rice. Dongbei rice is roundish and pearly<sup id="_ref-1" class="reference"><a href="http://www.wikihow.com/Make-Sushi-Rice#_note-1"><span style="color: #000000;">[1]</span></a></sup> and has the rare property of never reverting to the texture of uncooked rice after cooking i.e. will not harden but retains its soft texture, even after it becomes cold. This trait is essential for making authentic sushi and onigri. Dongbei rice is a high quality type of Chinese rice, while relatively expensive, it is still cheaper than "Sushi Rice" and can be found in quality/major Chinese grocery stores. The other alternative is to buy "Sushi Rice" online.</span></li>\r\n<li><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">A cheaper choice is Calrose, some brands are Botan Calrose and Kokuho Rose.</span></li>\r\n<li style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Other rice species will most likely be long-grain (commonly found in supermarkets) such as Basmati. Long-grain rice will not bind and is incapable of coming close to the taste and texture of sushi rice. Brown rice is unhulled wholegrain rice of any species. Brown rice is never used in authentic sushi making, but can be used for healthier eating.</span></li>\r\n</ul>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/rice/1.jpg" alt="1" width="500" /></p>\r\n<div class="step_num">&nbsp;</div>\r\n<div class="step" style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino;"><strong>Measure out the rice</strong>. <span style="color: #000000;">Depending on how hungry you are, if there are appetizers, desserts, whatever, 600 grams (1.32 pounds) should be adequate for four adults, if your meal includes an appetizer and perhaps something for dessert. 600 grammes is also often a very good portion for your regular-sized stove pot, in that you get about a half-filled pot of rice which seems to give the best result, moisture and texture wise. A <a href="http://www.wikihow.com/Cook-Rice-in-a-Rice-Cooker" title="Cook Rice in a Rice Cooker"><span style="color: #000000;">rice cooker</span></a> is the most dependable way of making rice. &nbsp;</span></span></div>\r\n<div class="step"><img style="display: block; margin: 10px auto;" src="images/articles/rice/2.jpg" alt="2" width="500" /></div>\r\n<div class="step" style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><strong>Next, rinse and soak the rice. </strong>One way to do this is by finding a really big pot that you can fill with lots of cold water. Rinse the rice by pouring lots of cold water onto it, then use your hands to move the rice about in the water bath so that it gives off as much as possible of those little dirt and starch particles that make the water all greyish. You don''t have to do it very long, just give everything a good tumbling, then pour the water out again, as much as possible. Alternatively, you can put the rice in a strainer and put the strainer in the pot; fill the pot with water, agitate the rice, then lift the strainer out of the pot so you can pour out the milky water. Do this some four or five times, until the water seems relatively clear. After the last rinse, pour fresh water over the rice one last time and leave it to soak for about half an hour. Some sources recommend letting the rice sit, drained, for thirty minutes to an hour.</span></div>\r\n<div class="step" style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/rice/3.jpg" alt="3" width="500" height="281" /></div>\r\n<p class="step" style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><strong class="whb">To boil, you''ll need 100 milliliters of cold water for every 100 grammes of rice - that is, the weight of the rice before you let it soak, which in this example would mean 600 milliliters (20.3 fl.oz.</strong>) of water as we started out with 600 grammes of rice. Whatever container you used to measure out the rice, use the same to measure out the water. <strong>Put the water with the rice in a pot or <a href="http://www.wikihow.com/Cook-Rice-in-a-Rice-Cooker" title="Cook Rice in a Rice Cooker"><span style="color: #000000;">rice cooker</span></a></strong>, put on the lid (not to remove it again until the rice is done), and you turn up the heat to high. If you are using a rice cooker, let it do its thing; skip the next two steps, and go straight to cooling the rice (once the rice has finished cooking, of course). There is also the possibility of making sushi rice in the oven, as described in the section below. Otherwise...</span></p>\r\n<p class="step" style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/rice/4.jpg" alt="4" width="500" /></p>\r\n<p class="step"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><strong class="whb">Watch over the pot until it starts to boil.</strong> You''d really prefer a pot with a glass lid here, so that you can see the bubbles, because removing the lid would let out the steam and interfere with the cooking process. Once it starts boiling, start your timer. You want seven minutes to pass with full heat under the pot. You''ll think "Oh no, it''s gonna burn in the bottom" and you''re partly right - some of the rice will stick to the bottom, but that''s fine, because we won''t be using that rice for the sushi anyway. It''s inevitable that some of the rice will stick to the bottom, but some seeds will have to die for the rest of them to be perfect.</span></p>\r\n<ul style="list-style-type: circle;">\r\n<li><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Don''t use a pot or rice cooker with Teflon or some other type of non-stick coating inside. We <strong>want</strong> the rice to get stuck to the bottom, because the alternative is a kind of crust at the bottom of the pot where the rice gets crispy, which tastes great in itself, but is really bad mixed in with the rest of the rice in a sushi maki roll or a piece of nigiri.</span></li>\r\n</ul>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/rice/5.jpg" alt="5" width="500" height="281" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><strong class="whb">After the seven minutes have passed, you should turn the heat down from max power to just enough power to let the rice simmer for an additional fifteen minutes.</strong> Remember: Never take off the lid or you will ruin the rice. After these last fifteen minutes, the rice is cooked. But, it''s definitely not done.</span></p>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/rice/6.jpg" alt="6" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><strong class="whb"><strong>Optional:</strong> Let the rice cool down</strong> if you don''t want it to get too sticky while you season it. The trouble about cooling it down is that we don''t want the rice to dry out by leaving it on the kitchen table reacting with the air, and we also want it to cool down rather quickly. A good tip is to use a couple of clean kitchen towels dampened with cold water (not wet!). Spread one on the table, spread the rice on top of it (remember not to scrape the bottom, we don''t want that half-burnt rice down there in our sushi), and place the other towel on top of the rice, so the air doesn''t dry it up. This way, you should be able to cool the rice in about an hour.</span></p>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/rice/7.jpg" alt="7" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><strong class="whb">Make the su.</strong> For those interested, the word sushi is actually a compound of the word su - which means vinegar - and the word shi - which means "skill of hand". So, sushi basically means something like mastering the art of vinegar. You need a good rice vinegar, some salt (please, coarse salt, not fine, too many additives are put in this stuff to keep it from clumping, and they don''t taste very good!) and some sugar. Because different brands of vinegar taste very different, it''s a good idea to sample your way through the process by tasting on it along the way. But, a good rule of thumb says that for each 100 millilitres (3.4 fl.oz.) of vinegar, you''ll need to add three tablespoons of sugar and one-and-a-half teaspoon of salt. Put it all in a pot and heat it up while stirring, until everything''s dissolved. Now, adjust the mixture by tasting it - too vinegary? Add sugar. Not enough taste? Add salt. Not quite there? Add vinegar. Then cool to room temperature.</span></p>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/rice/8.jpg" alt="8" width="500" /></p>\r\n<p class="step"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><strong class="whb">Mix the su and rice.</strong> Traditionally, this is done with a <em>hangiri</em>, which is a round, flat-bottom wooden tub or barrel, and a wooden paddle. Alternatively, you can use a baking pan or cookie sheet (but not aluminum foil - it''ll react with the vinegar). You toss the rice gently with the su, using a gentle chopping and flipping motion with a paddle, and let the heat escape (if you haven''t cooled the rice already). Otherwise, the rice will continue cooking in its own heat. You can also spread the rice so that it cools down faster, but be sure not to mash it!</span></p>\r\n<ul style="list-style-type: circle;">\r\n<li style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Adjust to taste. Add a little su, then stir around (gently) with a wooden spatula or spoon, taste. Not enough? Repeat. You''ll probably end up using somewhere between 100 and 250 millilitres of su for the portion we''re making here. Remember not to try to get too much taste or saltiness out of the rice by adding su - the reason we don''t use salt in the rice in the first place and don''t want the su to make the rice salty is that sushi is dipped in soy sauce which is very salty indeed.</span></li>\r\n<li style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Use the sushi rice at room temperature. If the rice is still warm, cover it with a damp cloth (so it doesn''t dry out) and leave it be until it reaches room temperature. Sushi tastes best when made from freshly cooked, unrefrigerated rice.</span></li>\r\n</ul>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/rice/9.jpg" alt="9" width="500" /></p>\r\n<p style="text-align: justify;"><strong class="whb">I<span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">f you must refrigerate, then reheat by gently steaming or microwaving with a piece of lettuce leaf or cling wrap lightly covering the rice (so that it does not dry out) until the texture returns to that of soft, freshly cooked rice.</span></strong><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"> If you are using sushi rice or Dongbei rice (which does not harden like other types), slight heating is enough. If refrigeration was light, returning it to room temperature may be adequate.</span></p>', '', 1, 8, '2015-03-13 12:03:22', 259, '', '2015-03-16 12:45:10', 259, 0, '0000-00-00 00:00:00', '2015-03-13 12:03:22', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 14, 1, '', '', 1, 18, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '');
INSERT INTO `sud_content` (`id`, `asset_id`, `title`, `alias`, `introtext`, `fulltext`, `state`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) VALUES
(4, 59, 'Fish and Shellfish', 'fish-and-shellfish', '<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><span lang="en-US">Fish and shellfish destined for being eaten as sushi must first and foremost be fresh. This is because in most cases they will be eaten raw. Knowing how to assess the freshness of a whole fish, pieces of fish meat, shellfish, and so on will improve your chances of finding the freshest, and therefore most delicious, fish at the fish market or the best preserved frozen fish at the supermarket.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><span lang="en-US">Freshness is of particular concern when considering whole fish. that is, those fish shipped with their internal organs intact. When examining such fish make sure that the scales are intact and glossy: the flesh, resilient; the eyes, black and clear, not bloody, the belly. firm; the gills, bright red. And, there should be no fishy smell! Bloody eyes and blackish gills are sure signs that the fish must be cooked before being eaten.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><span lang="en-US">When buying cuts of fish meat, make sure that the meat is firm and has a sheen, and that the blood at the cut or sliced sections is vivid red, As washing fish meat in water leaches its flavor and makeis the flesh crumbly, it''s best to buy a whole fish and dress it at home How to do that will be described shortly.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><span lang="en-US">Once you have purchased a whole fish, it should be dressed as&nbsp;</span><span lang="en-US">soon as possible. Once dressed it must be kept cool. If it is to be used within a short period of time, place it in your refrigerator after covering it with a damp cloth. If the dressed fish is to stay refriger&shy;ated for more than half a day, seal it in plastic wrap. If left uncov&shy;ered, dressed fish will dry out in the refrigerator.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><span lang="en-US"></span><span lang="en-US">The taste of frozen fish depends on how well or how poorly it has been thawed out. The very best way to thaw frozen fish is to place it overnight in your refrigerator and allow it to thaw out gradually. But, if you are in a hurry, thaw out the frozen fish in salted water: if it is a freshwater fish, place it in a solution of 4 cups of water and 11/2 teaspoons of salt; if it is a saltwater fish, 4 cups of water and 1 tablespoon of salt. This will check most flavor loss. Do not, however, thaw out the frozen fish completely. Once it can be cut with a knife it is sufficiently thawed out to use.</span></span></p>\r\n<h4 style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino;">Gutting and cutting</span></h4>\r\n<p class="zn-body__paragraph"><img style="margin: 15px; float: left;" src="images/articles/fish/senmai-oroshi.jpg" alt="senmai oroshi" width="230" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Fish should be gutted and washed as soon as it''s brought back to the kitchen and is best consumed the same day.&nbsp;The most common ways to cut a fish are san-mai-oroshi (three-piece cut) and go-mai-oroshi (five-piece cut).&nbsp;Both methods include slicing the middle section with the bone out, and separating it from the two side flaps.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Go-mai-oroshi cuts the two fillets further into four thinner slices.&nbsp;Twisser tools are used to remove small bones on fillets before they''re cut into small pieces for sushi.&nbsp;"Fish meat is very delicate," says Teranishi. "Every time you touch a fish, you''re damaging it."&nbsp;Less adventurous cooks may consider already-cut fillets at the market.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><span lang="en-US">&nbsp;On the picture -&nbsp;<span style="line-height: 26.6000003814697px; text-align: justify;">San-mai-oroshi (three-piece cut).</span></span></span></p>\r\n<h4><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><em style="line-height: 26.6000003814697px;">Awabi</em><span style="line-height: 26.6000003814697px;">&nbsp;(abalone)&nbsp;</span></span></h4>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/fish/awabi.png" alt="awabi" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Awabi (abalone) is a luxurious clam. When it is eaten raw, it tastes faintly like the sea, but its crispy texture is what stands out the most. It is delicious, but almost impossible to find on sushi menus as they are so hard to obtain due to strict California laws. On the Northern California coast, you must have a license to fish abalone during the open season (April to June), and even then you can only take a few at a time. If officers catch you with more than the allotted amount (the maximum number changes annually), they will fine you quite heavily.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Abalone cling to rocks and can take over ten years to grow to a consumable size! These days they are only half as big as they were 30 years ago, since they were nearly wiped out due to over-fishing. Find stats and good information about abalone <a href="http://www.dfg.ca.gov/marine/ab_info.asp"><span style="color: #000000;">here</span></a>. Kuni says that he gets his abalone from <a href="http://www.abalonefarm.com/"><span style="color: #000000;">a farm in Cayucos</span></a>, CA, near San Luis Obispo. As long as they&rsquo;re farmed, they&rsquo;re legal. He can only accept gifts of fished abalone to serve without charge.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Kuni serves awabi with&nbsp;kimo-joyu (soy sauce with guts). He takes the guts of the awabi and adds one part shoyu with one part mirin, and mixes it together in a suribachi (mortar and pestle)</span></p>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/fish/IMG_3748.jpg" alt="IMG 3748" width="500" /><img style="display: block; margin: 10px auto;" src="images/articles/fish/IMG_3787.jpg" alt="IMG 3787" width="500" /></p>\r\n<hr />\r\n<h4 style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Ark Shell (Akagai)</span></h4>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/fish/akagai-1.jpg" alt="akagai 1" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Arc shell is translated as "red clam" or <span style="font-style: italic;">akagai</span> in Japanese for its red flesh. It is high in hemoglobin&nbsp;and therefore gets its red color. When preparing, the clam is shucked and then the valves, and organs are removed and discarded, but the exterior mantle (membrane that surrounds the clam''s body) is saved.The flesh is then scrubbed with salt to rid the slippery body fluid. After this, the color of the arc shell becomes pink. The flesh is then butterflied and prepared into a <em>nigiri</em>.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">The arc shell has a deep flavor. It''s aromas are strong, but it is not overpowering. It is also very chewy, but soft enough to bite through.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">If the exterior mantle (<span style="font-style: italic;">Himo, </span>meaning string) of the arc shell is thick enough, then it is used for <a href="http://www.sushiencyclopedia.com/index.html"><span style="color: #000000;">sushi</span></a> as well. The mantle is someties tastier than the clam itself. It is crunchy and concentrated in flavors</span>.&nbsp;</p>\r\n<hr />\r\n<h4><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Cockle (Torigai)</span></h4>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/fish/cockle_sushi.jpg" alt="cockle sushi" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;" lang="EN-US">Cockle, or <em>torigai</em> is a very chewy shellfish that has sweet aromas with a hint of bitterness. They are shucked, and everything but the legs used for sushi. They are butterflied and quickly poached in hot water. The temperature of the simmering water is very important to keep the patterns on the cockle to make them resemble an orchid petal.</span></p>\r\n<hr />\r\n<h4 style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">&nbsp;Anago (Conger Eel)</span></h4>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/fish/anago.jpg" alt="anago" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Also called "Japanese conger."　Usually 35-50 cm, but can grow up to 1 meter in length. Looks like an ordinary eel. Has conspicuous white dots along its body.</span><br /><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">The name means "child of the hole," because these eels like to bury themselves in the sandy sea bottom with only their heads jutting out, or hide in dark crevices. Found in waters around Japan, Korea and in the East China Sea.　<span style="text-align: center;">Best from June to August ("Rainy Season delicacy").</span></span><br /><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Delicious as a topping on sushi (often after being boiled in seasoned stock, but anago is also eaten raw) or as tempura. Melts in the mouth with a savory sweetness. One of the favorite ingredients of sushi chefs. Conger eel is less oily than unagi. Kuro-anago, a black variety, grows up to one meter and is mainly used in kamaboko fish paste. Now that unagi (eel) is getting scarce and very expensive, anago is a good replacement.&nbsp;</span></p>\r\n<hr />\r\n<h4 style="text-align: justify;">&nbsp;<span style="font-size: 12pt; font-family: ''book antiqua'', palatino;">Eel (Unagi)</span></h4>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/fish/un0.jpg" alt="un0" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><span lang="EN-US">Eel is not part of the original <span style="font-style: italic;">Edo </span>style sushi menu.&nbsp;The reason for this is that<span style="font-style: italic;"> Edo </span>(old name of Tokyo) was famous for its fast food industry specializing in <span style="font-style: italic;">Edo </span>stylesushi, <span style="font-style: italic;">tempura</span>, <span style="font-style: italic;">soba</span>, and <span style="font-style: italic;">unagi, </span>or eel was no exception. During the <span style="font-style: italic;">Edo</span> period, eel were abundant in the Tokyo area and had a cuisine called the <span style="font-style: italic;">Edomae Unagi</span>. Many <em>unagi</em> chefs existed during the Edoperiod</span><span lang="EN-US">(a few of them still presently exist throughout Japan)</span><span lang="EN-US"> and it was considered a separate profession since it required much skill. Sushi chefs wouldn&rsquo;t dare to sell eel prepared by themselves because they knew they couldn''t beat the taste and would make a fool out of themselves. Renowned sushi restaurants in&nbsp;would usually buy prepared eel from professional <em>unagi </em>chefs.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;" lang="EN-US">Eel is a difficult fish to prepare. It is cooked when used as <a href="http://www.sushiencyclopedia.com/index.html"><span style="color: #000000;">sushi</span></a>, and never eaten raw. It has a sweet, but earthy aroma, and if it is&nbsp;prepared improperly, the flesh can smell, and become very tough and rubbery. The eel is first filleted, and then grilled on open flame. This gets rid of excess fat under the skin, which is where the unpleasant scent mostly comes from. Then it is steamed to make the meat fluffy and to further drain out the oils. After this, it is once again grilled on open flame while basting it many times with&nbsp;eel sauce (<span style="font-style: italic;">unagi no tare</span>) which is made from the eel trimmings, soy sauce, sugar, and <span style="font-style: italic;">sake </span>(rice wine).&nbsp; </span></p>\r\n<div style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Eel should be soft, fluffy, and very flaky. It is mild in fat even after the grilling and steaming, and that is what a good eel should taste like. It is pleasant on the palate and should never have a fishy or earthy aftertaste. Crushed Japanese green pepper corn called<em> sansho</em> is often served with eel at eel restaurants. While this is not usually used in sushi restaurants, it stimulates the sweet taste of the eel.</span><span lang="EN-US"><br /></span></div>\r\n<div style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/fish/unagi-future.jpg" alt="unagi future" width="500" /></div>\r\n<div style="text-align: justify;">&nbsp;</div>\r\n<div style="text-align: justify;">&nbsp;</div>\r\n<hr class="system-pagebreak" title="Page 2" alt="Page 2" />\r\n<h4><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Fish Eggs</span></h4>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/fish/eggs.jpg" alt="eggs" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Fish eggs, such as the large salmon roe you might be served at a sushi restaurant or the tiny black pearls of caviar often served on a blini with creme fraiche, are off the menu for many people who haven''t acquired the taste. But daring palates that savor the salty flavor and unique texture of fish eggs are also getting a potent boost of nutrients, including vitamin B-12 and omega-3 fatty acids. However, like all good things, fish eggs should be eaten in moderation because they are high in sodium and cholesterol.</span></p>\r\n<h2 class="header" style="text-align: justify;"><strong><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;" data-dmc="section-headline">Types of Fish Eggs</span></strong></h2>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Fish eggs come in a variety of sizes, shapes and colors. Caviar comes from sturgeon, a type of fish found around the world, but notably in the Caspian Sea and Ural River, where five of the six species living in those areas, including the beluga, are prized for their fish eggs. The large orange eggs you see at Japanese restaurants, called ikura, are also caviar, but they are more commonly referred to as salmon roe. Lumpfish and whitefish roe are other commonly consumed varieties of fish egg.</span></p>\r\n<h2 class="header" style="text-align: justify;"><strong><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;" data-dmc="section-headline">Calories, Fat, Protein and Carbs</span></strong></h2>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">One ounce of wild salmon roe contains 70 calories, 4 grams of fat, 9 grams of protein and 2.5 grams of carbohydrate. Saturated fat comprises 1 gram of the total fat content; the rest comes from healthy polyunsaturated fatty acids. Black caviar has a similar nutrient profile, with 74 calories, 5 grams of fat, 7 grams of protein and 1 gram of carbohydrate per 1-ounce serving. A little over 1 gram of the total fat content is from saturated fat. Whitefish eggs differ the most among the three types of fish eggs with 29 calories, a little less than 1 gram of fat, 4 grams of protein and 1.5 grams of carbs. Less than 0.2 gram of the total fat comes from saturated fat.</span></p>\r\n<hr />\r\n<h4 style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Flat Shell (Tairagai)</span></h4>\r\n<p style="text-align: justify;"><br /><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Flat shell IS triangular in shape. Its shell ranges from dark green to&nbsp;brown in color. As with scallops. only the adductor is eaten. Flat&nbsp;shells are packed and sold either whole and unshelled. or shelled.</span></p>\r\n<hr />\r\n<h4><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Flounder (Hirame)</span></h4>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/fish/hirame-shita.jpg" alt="hirame shita" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">&ldquo;Hirame&rdquo; could be translated in many ways depending of your country of origin: Flat Fish, Sole, Ｔurbot (although the latter should define &ldquo;karei&rdquo;) and what else. There are many varieties, wild or humanーfed. In Japanese, the names are numerous: Hirame, Shitabirame, Ooguchikarei, Oyanirami, etc.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Actually they can be divided into two main groups:</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">1)The Olive flounder or Bastard halibut (Paralichthys olivaceus; ) is a species of large-tooth flounder native to the north-western Pacific Ocean.</span><br /><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"> It is often referred to as the Japanese flatfish or Korea(n) flatfish (광어) when mentioned in the context of those countries.</span><br /><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"> It is the most common flatfish species raised in aquaculture in Korea. They are raised in Japan and China as well.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">2) Tonguefishes (shitabirame/舌平目in Japanese) are a family, Cynoglossidae, of flatfishes. They are distinguished by the presence of a long hook on the snout overhanging the mouth, and the absence of pectoral fins. Their eyes are both on the left side of their body, which also lacks a pelvic fin.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">The best season is Autumn to Winter. They are still available until Spring in Shizuoka Prefecture. Wild ones come from Hokkaido and Aomori. Human-fed ones mainly hail from Oita, Ehime, Mie, and Kagoshima Prefectures.</span></p>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/fish/hirame1.jpg" alt="hirame1" width="500" /></p>\r\n<hr />\r\n<h4 style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Gizzard Shad (Kohada)</span></h4>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/fish/kohada-1.jpg" alt="kohada 1" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Kohada/コハダ、or &ldquo;small skin&rdquo; actually is not the Japanese name of gizzard shad, but a generic name for the small fish as sashimi or sushi.</span><br /><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"> The real Japanese name for gizzard shad is konoshiro/コノシロ, and even the same fish goes by other names depending on its size:</span><br /><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"> -Up to 5 cm: shinko/シンコ</span><br /><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"> -Around 10 cm: kohada/コハダ</span><br /><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"> -More than 15 cm: konoshiro/コノシロ</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Although the mainfishing/angling season is around November~December, it can be found in good sushi restaurants all year round.</span><br /><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"> This said, in July, it will be the small shinko season.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Choose fresh specimens. Fish with reddish eyes and flaking scales should be avoided.</span><br /><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"> The fish is particularly popular pickled in salt and vinegar before being served either as sashimi or sushi, as the smell emanating form the grilled fish is too strong for many.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Kohada maki, with no rice, is an interesting morsel for people wishing to savour it alone with a great drink.</span></p>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/fish/kohada-maki.jpg" alt="kohada maki" width="500" /></p>\r\n<hr />\r\n<h4><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Horse Clam (Mirugai)</span></h4>\r\n<p style="text-align: justify;"><br /><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">A member of the found dam family. horse clam lives in a big&nbsp;shell from between which its long, chimneylike. muscular siphon&nbsp;reaches out of the ocean floor. It is the thick and crunchy siphon&nbsp;that is eaten as finger sushi. Other parts of the clam can also be&nbsp;eaten but their taste pales before that of the siphon. If the siphon&nbsp;meat of a horse clam is very firm and hard. you can be sure that it''s&nbsp;fresh.</span></p>\r\n<hr />\r\n<h4><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Mackerel (Saba)</span></h4>\r\n<div><img style="display: block; margin: 10px auto;" src="images/articles/fish/saba-1.jpg" alt="saba 1" width="500" />&nbsp;</div>\r\n<p><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"><span lang="EN-US">Makerel is grouped as a shiny fish, which are usually more economical in price for their abundance.</span><span lang="EN-US"> They are also known to spoil very quickly, and Mackerel is notorious for being the quickest one to do so. Therefore it is usually cured in salt for 3 to 4&nbsp;hours and then washed with rice vinegar. This is called <em>shime saba</em> or cured mackerel. The mackerel sushi is always prepared as so. Eating mackerel raw without professional advice is considered unsanitary. Mackerel can only be eaten raw right after it is caught; a delicacy enjoyed only by fishermen. However there are a few local fisherman in Japan (especially in the Kyushu region of southern Japan) that bring them in live, so they can be prepared as <a href="http://www.sushiencyclopedia.com/index.html"><span style="color: #000000;">sushi </span></a>or sashimi in its raw state, but it is a local delicacy and not common. Saba also requires quite a lot of skill to fillet, since the flesh breaks very easily.<br /> </span></span></p>\r\n<p><span style="color: #000000; font-family: ''book antiqua'', palatino; font-size: 12pt;"><span lang="EN-US"></span><span lang="EN-US">It is very rich in oil and has a slight dry aftertaste. Very high in omega-3 oils and Vitamin E. Considered one of the healthiest sushi, but not the most popular.&nbsp; Mackerel has been cured in Japan for centuries, and has become popular in inland cities, such as Kyoto, which is famous for their own stick type mackerel sushi called the bo-sushi or <a href="http://www.sushiencyclopedia.com/sushi_menu/saba_sushi.html"><span style="color: #000000;">bozuzshi</span></a>.</span></span></p>\r\n<div style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;" lang="EN-US">Wasabi should be enough to accentuate a properly cured mackerel, but many people worry about the mackerel''s overpowering aftertaste, and that can be due to over curing. This kills the aromas of the Mackerel and leaves it only with a fishy scent (fish oil). Grated ginger and sliced scallions are often accompanied for this reason.</span></div>\r\n<div style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/fish/saba-konbujime-nigiri.jpg" alt="saba konbujime nigiri" width="500" height="376" /></div>\r\n<p>&nbsp;</p>\r\n<hr />\r\n<h4><span style="font-family: ''book antiqua'', palatino; font-size: 12pt;">&nbsp;Mantis Shrimp (Shako)</span></h4>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/fish/shako-1a.jpg" alt="shako 1a" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Mantis shrimp is an original Edo style sushi ingredient. The mantis shrimp has to be poached immediately after it is caught, since it starts to digest its own body after it is fished out of the water.&nbsp;</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">The mantis shrimp is poached in hot water blended with rice wine and salt for about 10 min. It is either served alone, or with a <em>tusme</em> sauce made from its own broth. Mantis shrimp is fine in texture and although does not have a sweet aroma, it has a deeper flavor than any other shrimp or crab.</span></p>\r\n<hr /><hr class="system-pagebreak" title="Page 3" alt="Page 3" />\r\n<h4><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">&nbsp;Octopus (Tako)</span></h4>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/fish/Nigiri_tako.jpg" alt="Nigiri tako" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Octopus is always poached for <a href="http://www.sushiencyclopedia.com/index.html"><span style="color: #000000;">sushi</span></a>, and the leg is the section used. It is eaten raw as a sashimi plate, but it requires to be sliced very thinly because it is tough to chew on. The second reason it is cooked is because it is too bland when it is raw. It has sweet aromas, but it is very hard to taste.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">The octopus is first washed with salt by exfoliating the slippery texture that it has on its skin. Then it is massaged for a while and thumped with a peeled <a href="http://www.sushiencyclopedia.com/sushi_ingredients/daikon_radish.html"><span style="color: #000000;"><em>daikon</em> radish</span></a>. The radish&rsquo;s juices helps it become very soft when it is poached. If all these steps are ignored before poaching, the octopus will result in a chunk of rubber.After poaching, the octopus&rsquo;s sweet aromas are enhanced, though still very faint. Like the squid, it is hard to make into a<em> nigiri </em>because the slice is flat and stiff. A belt of <em>nori </em>is commonly used to fasten the octopus to the sushi rice. Some people like the crunchy texture of the tentacles, so they are sometimes all removed, one by one, and served as a boat roll or <em>gunkanmaki</em>.</span></p>\r\n<hr />\r\n<h4><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Round Clam (Aovagi or Bakagai)</span></h4>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/fish/aoyagi-1.jpg" alt="aoyagi 1" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">The round clam is a bivalve shellfish with a very fragile shell. It&nbsp;is usually sold out of its shell. the meat being separated from the&nbsp;adductor. The meat is eaten raw if it is very fresh. that is. if it&nbsp;shrinks at the touch of a inger. After a quick wash in salted water it&nbsp;can be used just as it is for topping finger sushi. If it is only moderately fresh. the meat should be blanched for a few seconds and then&nbsp;immediately cooled in cold water before eating. Round clam is at&nbsp;its best in winter and spring. The muscle, called kobashira,is sold&nbsp;separately from the meat and is considered to be one of the best&nbsp;toppings for finger sushi. Cut into small pieces and served on top of&nbsp;vinegared rice that has had nori wrapped around the sides,&nbsp;the kobashira is soft in texture. with a light taste.</span></p>\r\n<hr />\r\n<h4><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Scallop (Hotategai)</span></h4>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/fish/hotategai.jpg" alt="hotategai" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">The adductor of the scallop makes very tasty finger sushi. Scallops can be bought alive in their shells or already shelled. In the latter&nbsp;case, make sure the meat is very firm as this assures you of its&nbsp;freshness.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Scallop sushi is the softest <a href="http://www.sushiencyclopedia.com/index.html"><span style="color: #000000;">sushi</span></a> in the shellfish category. The sweetness of the scallop is easily tasted due to its high glycogen content. The muscle that connects the organs to the shell is used for the main <a href="http://www.sushiencyclopedia.com/sushi/sushi_ingredients.html"><span style="color: #000000;">sushi ingredient</span></a>. Other parts of the body are not used. Scallops have big cylindrical connector (or adductor) muscles since they can jump and swim near the ocean floor.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">The scallop is prepared by immersing them in saltwater with the same concentration of seawater. This way, they expel sand that is trapped in their system. After they are shelled the flat cylindrical muscle is butterflied just enough to barely keep the two pieces connected. The cut piece is then flattened out and made into a <em>nigiri</em>. If the scallop is barely grilled, the sweetness increases, however, the fresh texture is lost. To intensify the sweetness of the scallop, it is best to use <span style="font-style: italic;">wasabi</span>, but sea salt, and a drop of citrus works good as well instead of soy sauce.</span></p>\r\n<hr />\r\n<h4><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Sea Bass (Suzuki)</span></h4>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/fish/suzuki-1.jpg" alt="suzuki 1" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Sea bass represents the white fishes during the summer. The black sea bream is also a white fish that is in season during the summer, but less favorable when compared to the sea bass. It is mild in fat and has a tough flesh compared to other white fishes. For this reason it is sliced thinner.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">In order to make the sea bass more refreshing some of the fat is taken out by quickly immersing sliced flesh into hot water blended with rice wine (<em>yubiki</em> method). It is then immediately shocked in ice water and the resulting flesh becomes lighter in fat so the so the flavors of the sea bass are pulled out, and the flesh becomes a little softer. Sea bass has always been enjoyed with a little chopped <a href="http://www.sushiencyclopedia.com/sushi_condiments/umeboshi.html"><span style="color: #000000;">pickled plum</span></a>, since the sour flavors bring out the sweetness more than wasabi does for its case.</span></p>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/fish/suzuki-sashimi-aburi.jpg" alt="suzuki sashimi aburi" width="500" /></p>\r\n<hr />\r\n<h4 style="text-align: justify;">&nbsp;<span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Sea Bream (Tai)</span></h4>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/fish/madai1.jpg" alt="madai1" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">There are many varieties of Seabream/Snappers/Tai/鯛in Japan:</span><br /><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"> Batodai, Hanadai, Ishidai, Kinmeidai, Mekkidai, etc., but the most popular variety is called Madai/真鯛, or True Seabream.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Even Madai/True Seabream is called different names depending upon the region: Oodai or Hondai.</span><br /><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"> The best season streches from Winter to Spring.</span><br /><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"> It is extensively raised by humans in Ehime, Mie and Saga Prefectures.</span><br /><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"> Wild specimen are mainly caught off the shores of Nagasaki, Fukuok, Kumamoto, and Yamaguchi Prefectures. Not so many Madai are caught in Shizuoka but other varieties are abundant especially around Izu Penisula.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Human-raised amount to over 80,000 tonnes a year wild ones are caught at a rate inferior to 15,000 tonnes a year.</span><br /><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"> Imports are relatively and account for only about 6.500 tonnes a year.</span></p>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/fish/madai-sashimi.jpg" alt="madai sashimi" width="500" /></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<hr />\r\n<h4><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Sea Urchin (Uni)</span></h4>\r\n<p><img style="display: block; margin: 10px auto;" src="images/articles/fish/ur1.jpg" alt="ur1" width="500" /></p>\r\n<div id="stcpDiv" style="position: absolute; top: -1999px; left: -1988px;">\r\n<p data-iceapc="1" data-iceapw="200">Uni (oo-nee) is the Japanese name for the <span id="IL_AD7" class="IL_AD">edible</span> part of the <strong><a href="http://www.sushinut.com" target="_blank">Sea Urchin</a></strong>. While colloquially referred to as the roe (eggs), <a href="http://www.sushinut.com">uni</a> is actually the animal&rsquo;s gonads (which produce the milt or roe). Uni ranges in color from rich gold to light yellow, and has a creamy consistency that some love and is off-putting to others. It is nevertheless one <a href="http://www.sushifaq.com/">sushi</a> item that is in incredible demand around the world, which is reflected in its price. <span id="IL_AD4" class="IL_AD">Sea Urchins</span> are a rare treat for those who acquire a taste. Uni sushi has <span id="IL_AD8" class="IL_AD">a light</span>, sweet, and <span id="IL_AD11" class="IL_AD">somewhat</span> briny flavor and is is usually enjoyed as nigiri sushi or <a href="http://www.sushifaq.com/basic_recipe/sashimi/">sashimi</a>. Uni is also sometimes served with a raw quail egg. Uni is also considered an aphrodisiac by some. It is harvested throughout the world, however <span id="IL_AD10" class="IL_AD">the west</span> coast of the U.S. has in recent years become one of the largest sources, and much of the harvest finds its way <span id="IL_AD3" class="IL_AD">overseas</span> to eager Asian customers. Each <span id="IL_AD1" class="IL_AD">sea urchin</span> contains five &lsquo;lobes&rsquo; and the harvesting of the uni is a very <span id="IL_AD9" class="IL_AD">delicate</span> process as the meat easily falls apart. Uni is a somewhat seasonal item and is at its best when served from late fall through the winter, with December considered the best month by Japanese standards.</p>\r\n<h2>The Different Grades of Uni sushi</h2>\r\n<div class="thickborder" style="float: right; width: 254px; height: 254px;" data-iceapc="5">\r\n<table border="0" cellpadding="2" data-iceapc="4">\r\n<tbody data-iceapc="3">\r\n<tr data-iceapc="2">\r\n<td data-iceapc="1"><img title="uni - sea urchin" src="http://i2.wp.com/www.sushifaq.com/wp-content/uploads/2011/09/uni-sea-urchin.jpg?resize=275%2C197" alt="uni - sea urchin" border="0" data-recalc-dims="1" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<p data-iceapc="7" data-iceapw="207">Uni has historically been graded based on color, texture, and freshness. The highest grade is a bright yellow/gold (Grade A) with a firm texture and somewhat sweet. Grade B uni is a more muted yellow and has a softer texture and is less sweet, while Grade C uni is referred to as &lsquo;vana&rsquo; and is often the parts left over from uni that has broken apart during processing or handling. Obviously, the higher the grade, the higher the price, and fresh uni taken directly from a living sea urchin will command the highest price (and is worth the experience as uni this fresh is noticeably different from uni that was processed 24 plus hours <span id="IL_AD12" class="IL_AD">beforehand</span>). Uni is also available fresh (<em data-iceapw="2">nama uni</em>), frozen (<em data-iceapw="2">reito uni</em>), baked and frozen (<em data-iceapw="2">yaki uni</em>), steamed (<em data-iceapw="2">mushi uni</em>), and salted (<em data-iceapw="2">shio uni</em>), which is usually reserved for the lower <span id="IL_AD6" class="IL_AD">grades</span> of uni. Uni is also available as <em data-iceapw="2">neri uni</em> (blended urchin paste) and <em data-iceapw="2">tsubi uni</em> (a lumpy paste). The color and quality of uni is largely dependant on its diet, gender, and time of harvest. Size is also important as some lobes of uni can be too large for a piece of sushi.</p>\r\n- See more at: <a href="http://www.sushifaq.com/sushi-sashimi-info/sushi-item-profiles/sushi-items-uni-sea-urchin/#sthash.vzcj3vaF.dpuf">http://www.sushifaq.com/sushi-sashimi-info/sushi-item-profiles/sushi-items-uni-sea-urchin/#sthash.vzcj3vaF.dpuf</a></div>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Sea urchin is one of the top three delicacies in Japan along with mullet roe (<span style="font-style: italic;">Karasumi</span>), and cured sea cucumber intestines(<span style="font-style: italic;">Konowata</span>). Sea urchin sushi uses the gonads of the sea urchin. The colors range from yellow to bright orange. This<a href="http://www.sushiencyclopedia.com/index.html"><span style="color: #000000;"> sushi</span></a>&nbsp;was unavailable until the ship roll was invented, since it was too soft to make into a<a href="http://www.sushiencyclopedia.com/how_to_make_sushi/how_to_make_nigiri_sushi.html"><span style="font-style: italic; color: #000000;"> nigiri</span></a>. Before the ship roll was invented,&nbsp;they were simply topped on rice placed in a bowl along with some condiments&nbsp;and enjoyed as a whole meal.&nbsp;</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Sea urchin has a strong ocean scent, and it is very creamy and rich. It is prepared as a ship roll or <span style="font-style: italic;">gunkanmiaki</span>, however some people prefer not to use nori for the ship roll and ask to be <a href="http://www.sushiencyclopedia.com/blog/2007/10/12/the-sea-urchin-sushi-dilemma/"><span style="color: #000000;">rolled with paper thin Japanese cucumber strips</span></a>. The main reason for this is that seaweed has another kind of different ocean scent and it competes with the sea urchin. Using cucumbers instead makes it possible to taste the whole sea urchin. Quail egg is often placed inside the ship roll, but it is not recommended for it kills the flavors of the sea urchin.&nbsp;It should be used only if you want to weaken the aromas of the sea urchin. Recently, many chefs have begun to make the <a href="http://www.sushiencyclopedia.com/blog/2007/10/12/the-sea-urchin-sushi-dilemma/"><span style="color: #000000;">sea urchin nigiri</span></a> by tightening the flesh with cirtus juice. This slighly constricts the flesh so it is possible to make it into a nigiri style, and it is condidered the best way to eat sea urchin sushi.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">When seasoning the sea urchin sushi, a few drops of soy sauce should be applied on top of the sea urchin directly from the soy sauce dispenser.&nbsp;</span></p>\r\n<p style="text-align: justify;">&nbsp;<img style="display: block; margin: 10px auto;" src="images/articles/fish/seaurchin1.jpg" alt="seaurchin1" width="500" /></p>\r\n<hr />\r\n<h4 style="text-align: justify;">&nbsp;<span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Shrimp (ebi)</span></h4>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/fish/Ebi2_web-612x343.jpg" alt="Ebi2 web 612x343" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;" lang="EN-US">Shrimp is one of the original <em>Edomae </em><a href="http://www.sushiencyclopedia.com/sushi/sushi_ingredients.html"><span style="color: #000000;">sushi ingredients</span></a>, and it is usually poached before it is prepared although it is better steamed, since it locks the flavors inside. It is a very basic<a href="http://www.sushiencyclopedia.com/index.html"><span style="color: #000000;"> sushi</span></a> that doesn&rsquo;t get the reputation that it deserves. Originally it was made with the now pricey <a href="http://www.sushiencyclopedia.com/blog/2007/10/13/real-shrimp-sushi/"><span style="color: #000000;"><em>kuruma</em> shrimp</span></a> (wheel shrimp). The <em>kuruma</em> shrimp is&nbsp; juicer, and has a sweeter aroma and competes well with a&nbsp;lobster. Poor preparation due to negligence, and the recent use of smaller shrimps&nbsp;(such as bay shrimp) has made the shrimp sushi become rather ordinary. The Black Tiger shrimp is the best alternative to the kuruma shrimp since they are in the same genus. Though a bit smaller, and lighter in taste, it can be made into a fine sushi.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Although shrimp may be considered the economical sushi, there really are no ranks within sushi ingredients. All ingredients were prepared and equally treated to bring out the true flavors to its limit. Shrimp is considered one of the tastiest ingredients in sushi, if it is prepared correctly. It is sad to see less and less true shrimp sushi these days.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">It is a delicacy for shrimp to be prepared raw in sushi. Though some connoisseurs may go after it for the fresh taste, shrimp is considered to be the tastiest when lightly heated and medium rare, since the sweet flavor is magnified. If it is poached too long, the flesh becomes too hard, and the flavors are bled out into the boiling water and you are left with a rubbery piece of shrimp. When consumed raw (which has to be extra fresh), shrimp has a slight metallic aftertaste that lingers on your palate and can mask the tastes of the following sushi that you may eat. Therefore raw shrimp is usually served as a sashimi in Japanese cuisine, prepared by lightly washing it in chilled water (<em>arai</em> method) and served with citrus and herbs to counter the after taste.</span></p>\r\n<hr />\r\n<h4 style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"></span><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Squid (Ika)</span></h4>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/fish/ika-meishi-2.jpg" alt="ika meishi 2" width="500" /></p>\r\n<p style="text-align: justify;">I<span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">n the past only cooked squid was eaten as sushi or sashimi, but&nbsp;today''s modern fishing techniques and heezing methods make it&nbsp;possible to enjoy the taste of raw squid.</span></p>\r\n<hr />\r\n<h4 style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">&nbsp;Tuna (Maguro)</span></h4>\r\n<p style="text-align: justify;"><img style="display: block; margin: 10px auto;" src="images/articles/fish/tuna.jpg" alt="tuna" width="500" /></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"></span></p>\r\n<p><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;" lang="EN-US">Tuna is the most basic <em>Edo </em>style sushi. It is one of the oldest ingredients used, and often regarded as the icon of&nbsp;<a href="http://www.sushiencyclopedia.com/index.html"><span style="color: #000000;">sushi</span></a>. </span></p>\r\n<p><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"></span></p>\r\n<div style="text-align: justify;">\r\n<div><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;" lang="EN-US">The&nbsp;tuna has a simple, but robust taste. It has almost no fat, and has a firm flesh. The <a href="http://www.sushiencyclopedia.com/sushi_fish/tuna.html"><span style="color: #000000;">blue fin tuna</span></a> or <em>honmaguro</em> (true tuna), nicknamed the &ldquo;black diamond&rdquo; (for its shiny cobalt skin), is considered the best match for <em>Edo </em>stylesushi, and is appreciated much more than the yellow fin or ahi tuna. The yellow fin lives in the warm currents and its flesh is bland, due to high water content, and soft compared to the blue fin which lives in the cold currents. The yellow fin tuna has little or no fatty section as well. This is a characteristic of fish that dwell in warm waters. The blue fin tuna has a firm flesh, the best tuna belly section, and has a deeper red color compared to the bright ruby tone of the yellow tail.</span></div>\r\n<span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;" lang="EN-US">Tuna is best to eat it in its most simplest form. <em>Wasabi</em>, and soy sauce is the best match. No special touches are required. It is usually ordered after the lighter white fishes, and before the richer ingredients such as <a href="http://www.sushiencyclopedia.com/sushi_fish/spanish_mackerel.html"><span style="color: #000000;">Spanish mackerel</span></a>, <a href="http://www.sushiencyclopedia.com/sushi_fish/tuna_belly.html"><span style="color: #000000;">tuna belly</span></a>, <a href="http://www.sushiencyclopedia.com/sushi_roe/sea_urchin.html"><span style="color: #000000;">sea urchin</span></a>, and <a href="http://www.sushiencyclopedia.com/sushi_roe/salmon_roe.html"><span style="color: #000000;">salmon roe</span></a>. The old <em><a href="http://www.sushiencyclopedia.com/blog/2007/10/09/soaked-fish/"><span style="color: #000000;">zuke</span></a> </em>method, where the sliced sashimi is soaked in soy sauce for a short period of time, is a popular choice. This was done in the old days to prevent the tuna from spoiling too quickly, but it concentrates the flavors in the tuna flesh due to water loss by osmosis. Tuna is also popular for the <em>tekka maki </em>(flaming steel roll), the most basic <em>Edo </em>Style roll.</span></div>\r\n<div style="text-align: justify;"><hr /></div>\r\n<h4><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"></span><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Yellowtail (Hamachi or Buri)</span></h4>\r\n<p><img src="images/articles/fish/burifilets.jpg" width="500" alt="burifilets" style="display: block; margin: 10px auto;" /></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;">The yellowtail is included in a group of <a href="http://www.sushiencyclopedia.com/sushi_ingredients/sushi_fish.html"><span style="color: #000000;">sushi fish</span></a> called the &ldquo;Big Three Yellowtails&rdquo;. They consist of <a href="http://www.sushiencyclopedia.com/sushi_fish/yellowtail.html"><span style="color: #000000;">yellowtail</span></a>, <a href="http://www.sushiencyclopedia.com/sushi_fish/yellowtail_amberjack.html"><span style="color: #000000;">yellowtail amberjack</span></a>, and<a href="http://www.sushiencyclopedia.com/sushi_fish/amberjack.html"><span style="color: #000000;"> amberjack</span></a>. These three fishes are classed as a luxury fish. They are all related, and similar in nature, but the yellowtail is considered the most valuable. It is best during the winter, when it has a very oily texture. The flesh is light red, and rich with deep flavors.</span></p>\r\n<div style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;">When speaking of<a href="http://www.sushiencyclopedia.com/index.html"><span style="color: #000000;"> sushi</span></a>, the&nbsp;<em>hamachi </em>is incorrectly referred as the yellowtail (<span style="font-style: italic;">buri</span>) very often. They are both the same, however, <em>hamachi </em>is the farmed version of yellowtail. (please see <a href="http://www.sushiencyclopedia.com/sushi_fish/yellowtail.html"><span style="color: #000000;">description of yellowtail</span></a>).</span></div>\r\n<div style="text-align: justify;"><span lang="EN-US" style="font-family: ''book antiqua'', palatino; font-size: 12pt; color: #000000;">The yellowtail sushi during its best season has difficulty seasoning soy sauce to it for its high oil content. The soy sauce literally gets ''repelled'' off the fish. Although the yellowtail is rich, the oils have a clear, and sweet aftertaste which is accompanied with a bold taste of the flesh.&nbsp;</span></div>\r\n<div style="text-align: justify;"><hr /></div>\r\n<div style="text-align: justify;">&nbsp;</div>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"></span></p>\r\n<p style="text-align: justify;"><span style="font-family: ''book antiqua'', palatino; color: #000000; font-size: 12pt;"></span></p>\r\n<div id="stcpDiv" style="position: absolute; top: -1999px; left: -1988px;">Awabi is the Japanese word for Abalone, which are edible sea snails of the Halliotidae family. Know as the &ldquo;king of clams&rdquo;, these mollusks have vibrantly colored shells which hide the delicacy within. When served raw, the male abalone is preferred, whereas the female is best suited to cooking or steaming. The flesh of the abalone is muscle meat, which results in a firm texture. The flesh is actually the foot muscle, which allows the abalone to propel itself (slowly) in the wild. In Japanese culture, abalone is consumed raw, steamed, salted, boiled, or simmered in soya sauce. When used for sushi, the ingredient is either raw or even live. The taste of awabi is delicate and subtle, and as such is best served without accompaniment. The slightly rubbery texture of the ingredient lends to raw consumption, making it a perfect sushi item. Cooking abalone will only increase the toughness of the already firm meat. Awabi, when served as sashimi, will often be separated from its shell and cut thinly, against the grain of the meat. Because of the firmness of the meat, awabi can be lightly scored to increase tenderness. True lovers of abalone will eat the delicacy as it is, or coat it lightly with soy sauce. Live abalone for sushi is of the highest grade, called Ezo Awabi, and commands a respectable price tag. - See more at: <a href="http://www.sushifaq.com/sushi-sashimi-info/sushi-item-profiles/awabi-abalone/#sthash.zcxsY30W.dpuf">http://www.sushifaq.com/sushi-sashimi-info/sushi-item-profiles/awabi-abalone/#sthash.zcxsY30W.dpuf</a></div>\r\n<div id="stcpDiv" style="position: absolute; top: -1999px; left: -1988px;">Awabi (abalone)</div>', '', 1, 8, '2015-03-16 13:10:14', 259, '', '2015-03-18 01:16:05', 259, 0, '0000-00-00 00:00:00', '2015-03-16 13:10:14', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 37, 0, '', '', 1, 34, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '');
INSERT INTO `sud_content` (`id`, `asset_id`, `title`, `alias`, `introtext`, `fulltext`, `state`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) VALUES
(5, 61, 'Essential Tools For Sushi Making', 'essential-tools-for-sushi-making', '<h3><span style="color: #000000; font-family: ''book antiqua'', palatino; font-size: 12pt; text-align: justify; line-height: 1.9em;">Sushi is a delicious treat with a base of specially prepared rice and other ingredients, such as raw fish, cooked fish, imitation crab, seaweed, avocado and cucumber. Although sushi originated in Japan, this delectable cuisine is now enjoyed in nations across the world.</span></h3>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">However, good quality sushi tends to be pricey, so satisfying any cravings at restaurants can be detrimental to an avid sushi fan''s finances. Reduce your sushi spending by making, serving and enjoying it at home. Besides the proper ingredients, you will need the following tools:</span></p>\r\n<h2 style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><strong>1. Rice Cooker</strong></span></h2>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Rice is the main ingredient of all types of sushi. Thinly sliced raw fish without rice is often mistaken as sushi, but is actually called &ldquo;sashimi.&rdquo;</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">To make sushi rice, which is rice with salt, sugar, rice vinegar and possibly sake, you will need a <a href="http://www.smarter.com/topic/how-to-buy-a-rice-cooker.html"><span style="color: #000000;">rice cooker</span></a>. A rice cooker makes for perfectly cooked white rice, and you can also enjoy extra features such as an attached steamer or a &ldquo;warming&rdquo; function after the rice has already cooked. If you would rather not spend money on a <a href="http://www.smarter.com/se--qq-rice%2Bcooker.html"><span style="color: #000000;">rice cooker</span></a>, you can also boil and cook rice in a medium sized <a href="http://www.smarter.com/se--qq-medium%2Bsaucepan.html"><span style="color: #000000;">saucepan</span></a>.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">For extra-clean rice, consider purchasing a specialized <a href="http://www.smarter.com/stats/scripts/redir.php?bt=1&amp;&amp;cd=69&amp;pi=0&amp;mi=301531&amp;ca=0&amp;ds=U0hPUFBJTkc%3D&amp;cc=q-ry&amp;pdp=1&amp;plt=grid"><span style="color: #000000;">rice washing bowl</span></a>. Regular colanders and strainers tend to be unsuitable and frustrating because the holes may be too large, permitting rice to escape when under pressurized water flow.</span></p>\r\n<h2 style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><strong>2. Hangiri/Wooden Rice Bowl</strong></span></h2>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">A <a href="http://www.smarter.com/se--qq-hangiri.html"><span style="color: #000000;">hangiri</span></a> (also known as handai) is a flat-bottomed, round wooden barrel or tub used in traditional sushi making. Made of cypress wood and bound with two copper bands, it absorbs moisture and quickly cools down freshly cooked rice. It also serves as a container to toss regular, cooked white rice with ingredients necessary to make superior quality sushi rice. Use a <a href="http://www.smarter.com/se--qq-wooden%2Brice%2Bpaddle.html"><span style="color: #000000;">wooden rice paddle</span></a> as a tool to help the tossing and mixing process.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Proper care of a hangiri includes washing with soapy water immediately after use and storing in an area with adequate air circulation for proper drying and mold prevention.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Because hangiris can be pricy, a <a href="http://www.smarter.com/stats/scripts/redir.php?bt=1&amp;%3D&amp;cd=69&amp;pi=0&amp;mi=40085&amp;ca=0&amp;ds=U0hPUFBJTkc%3D&amp;cc=g-qo&amp;pdp=2&amp;plt=grid"><span style="color: #000000;">large plastic bowl</span></a> is a suitable alternative. However, avoid metallic bowls because vinegar can react with metal and cause a disagreeable taste.</span></p>\r\n<h2 style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><strong>3. Bamboo Rolling Mat</strong></span></h2>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">A rolling mat called a &ldquo;<a href="http://www.smarter.com/se--qq-makisu.html"><span style="color: #000000;">makisu</span></a>&rdquo; consists of thin bamboo strips tied together with cotton string. When used, this mat helps to uphold even pressure to all sides of the sushi. These bamboo mats usually measure about 9.5 inches by 9.5 inches, but sizes can vary. If offered a mat with thicker bamboo strips and thinner bamboo strips, go for the more versatile, thick mat. Also, look for a rolling mat with one flat side and one rounded side for easier use.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">A rolling mat is typically used to roll nori (toasted seaweed sheets) around sushi rice and fillings. If preparing an inside-out roll (rice on the outside and the nori on the inside), wrap the mat with <a href="http://www.smarter.com/stats/scripts/redir.php?bt=1&amp;%3D&amp;cd=69&amp;pi=0&amp;mi=301531&amp;ca=0&amp;ds=U0hPUFBJTkc%3D&amp;cc=g-in&amp;pdp=16&amp;plt=grid"><span style="color: #000000;">plastic wrap</span></a> so rice does not stick toit.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">To prevent bacteria and mold growth, be sure to clean the bamboo mat meticulously by rinsing it with hot, soapy water and then air drying it thoroughly.</span></p>\r\n<h2 style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><strong>4. Sushi Roll Molds</strong></span></h2>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">If the bamboo rolling mat method isn&rsquo;t quite cutting it, a plastic mold will make things easier. This <a href="http://www.smarter.com/se--qq-Wooden%2BSushi%2BPress%2BMold%2BOshizushi%2BHako.html"><span style="color: #000000;">wooden sushi press mold</span></a> is made of pine wood and three components. Assemble the walls and the bottom, add rice and toppings and then use the top to press into the sushi. Finally, remove the top to cut the sushi before serving.&nbsp;</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">For a larger and round sushi roll, try the <a href="http://www.smarter.com/se--qq-Sushezi%2BSushi%2BMade%2BEasy.html"><span style="color: #000000;">Sushezi</span></a> for &ldquo;perfect sushi make easy.&rdquo; The plastic press mold even works for other rolled goods, such as fudge rolls and cake rolls.</span></p>\r\n<h2 style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><strong>5. Sashimi Knife</strong></span></h2>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">To cut blocks of raw fish, it&rsquo;s best to use a proper knife specially made for the job. A stainless steel, <a href="http://www.smarter.com/se--qq-sashimi%2Bknife.html"><span style="color: #000000;">sashimi knife</span></a> possesses a razor sharp edge to neatly and quickly slice through raw fish. These Japanese knives typically are only honed on the right side. Quality pieces run up to $400, but a cheaper and more basic sashimi knife is adequate for beginner sushi making.</span></p>\r\n<h2 style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><strong>6. Sushi Serving And Dinnerware</strong></span></h2>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Once you have made your sushi masterpiece, don&rsquo;t forget an equally beautiful way to serve the meal. This <a href="http://www.smarter.com/se--qq-Joyce%2BChen%2B55-1106%252C%2BBamboo%2BSushi%2BBoard%2BSet%2B6%2Binch%2Bby%2B10-1%252F2%2Binch.html"><span style="color: #000000;">bamboo sushi board set</span></a> includes a 6- by 10.5-inch burnished bamboo board, a porcelain sauce dish and chopsticks. Or if you will have a guest joining you, check out this <a href="http://www.smarter.com/se--qq-sushi%2Bservice%2Bfor%2Btwo.html"><span style="color: #000000;">Sushi Service for Two set</span></a>, which comes with two serving plates, two dipping bowls and two sets of chopsticks. The decorative set has an adorable figurine that fits in with the overall dining experience.</span></p>\r\n<h2 style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;"><strong>General Tips</strong></span></h2>\r\n<ul style="text-align: justify;">\r\n<li><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">When working with and eating raw fish, always buy sushi grade fish and other seafood. Avoid cross contamination and exposure to any bacteria by careful handling of raw fish and utensils/dishware that touch the raw fish.</span></li>\r\n<li><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Keep hands moist when making sushi to prevent rice from sticking to skin.</span></li>\r\n<li><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Keep a small bowl or container of water nearby to moisten the nori and keep a filled roll closed.</span></li>\r\n<li><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Do not overstuff rolls with excessive fillings; otherwise, the roll will not hold a neat, desired shape.</span></li>\r\n<li><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">When using the bamboo mat, press the mat gently over the roll with an even pressure.</span></li>\r\n<li><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">When using the sashimi knife, slightly wet the blade to aid in a clean cut and to keep from crushing the roll.</span></li>\r\n</ul>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; font-family: ''book antiqua'', palatino; color: #000000;">Make sushi at home for a more affordable meal and enjoy it within the confines of your own home. Complete your homemade meal with traditional Japanese green tea or even the fun activity of sake bombs!&nbsp;</span></p>\r\n<p style="text-align: justify;">&nbsp;</p>', '', 1, 2, '2015-03-18 14:45:54', 259, '', '2015-03-18 14:45:54', 0, 0, '0000-00-00 00:00:00', '2015-03-18 14:45:54', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 1, 0, '', '', 1, 4, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '');

